    function megaSchoolMenu() {
          $.schoolListMinibase({
            'miniBaseDomainID': 4,
            'miniBaseModuleInstanceID': 73723,
            'cache': true,
            'schoolListReady': function(schoolList) {
            	var schools = '', sortedSchools = {};

                // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED TYPES IN THE ORDER WE WANT
                var expectedNameOrder = ["elementary", "middle", "high", "specialty"];
                schoolList.sort(function(a, b) {
                	if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                    	return -1;
                    }
                    if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                    	return 1;
                    }
                    return 0;
                });

                // SORT THE SCHOOLS INTO TYPE OBJECTS
                for(var type in schoolList) {
                	if(schoolList[type].schoolVisible.toLowerCase() == 'true') {
                        var thisType = schoolList[type].schoolType.toLowerCase();

                        // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                        if(schoolList[type].schoolType.indexOf(",") > -1) {
                            var subTypes = schoolList[type].schoolType.split(",");

                            // LOOP THROUGH THE SUB TYPES
                            for(var subType in subTypes) {
                                var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                                // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                if(!sortedSchools.hasOwnProperty(thisSubType)) {
                                    // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                    sortedSchools[thisSubType] = [schoolList[type]];
                                } else {
                                    // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                    sortedSchools[thisSubType].push(schoolList[type]);
                                }
                            }
                        }

                        // SINGLE TYPE VALUE
                        else {
                            // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                            if(!sortedSchools.hasOwnProperty(thisType)) {
                                // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                sortedSchools[thisType] = [schoolList[type]];
                            } else {
                                // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                sortedSchools[thisType].push(schoolList[type]);
                            }
                        }
                    }
                }



                // BUILD THE SCHOOL MENU STRUCTURE
                var schoolMenuTabs = '';
                var schoolMenuPanels = '

';
                $.each(sortedSchools, function(type, schools) {
                	var typeName = type.replace(/\s/g, "-").replace(/&/g, "and");

                    // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
                    schools.sort(function(a, b) {
                    	if(a.schoolName > b.schoolName) {
                        	return 1;
                        }
                        if(a.schoolName < b.schoolName) {
                        	return -1;
                        }
                        return 0;
                    });
                    // BUILD TAB
                    schoolMenuTabs += '
' + typeName + '';

                    // BUILD PANEL
                    schoolMenuPanels += '
';
                    for(var school in schools) {
                    	schoolMenuPanels += '
' + schools[school].schoolName + '';
                    }
                    schoolMenuPanels += '';
                });
                schoolMenuTabs += '';
                schoolMenuPanels += '';

                // ADD THE COMPLETED MENU TO THE DOM
                $(".cs-custom-schoollist-btn .sw-dropdown").html(schoolMenuTabs + schoolMenuPanels).addClass("cs-schoollist-menu").attr("id","cs-custom-schoollist");
                $("#gb-school-list-tabs .gb-school-list-tab:eq(0)").addClass("active");
                $("#gb-school-list-panels-inner .gb-school-list-panel:eq(0)").attr("aria-hidden","false");
                $(".cs-schoollist-menu").appendTo("#gb-mystart");

                // BIND MENU EVENTS
                schoolMenuControls();

                // BUILD THE RESPONSIVE MENU
                csRsMenu(sortedSchools);
            }
        });
    }

    function schoolMenuControls() {
        // SCHOOLS BUTTON CLICK
        //$(".sw-mystart-dropdown.schoollist").unbind();
        //$(".sw-mystart-dropdown.schoollist").off();
        $(document).on("click keydown", ".cs-custom-schoollist-btn", function(e) {
            if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();

                //handleSchoolMenuButtonAction();
                if($(this).hasClass("open")) {
                	$(this).removeClass("open");
                    $(".sw-dropdown.cs-schoollist-menu").slideUp().attr({
                        "aria-hidden": "true"
                    });
                } else {
                	$(this).addClass("open");
                    $(".sw-dropdown.cs-schoollist-menu").slideDown().attr({
                        "aria-hidden": "false"
                    });
                    $(".gb-school-list-tab:eq(0)").focus();
                }
            }
        });

        $(document).on("click keydown", ".cs-close-school-menu", function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();
                $(".cs-custom-schoollist-btn").removeClass("open");
                $(".sw-dropdown.cs-schoollist-menu").slideUp().attr({
                    "aria-hidden": "true"
                });
            }
        });

        $(".sw-mystart-dropdown.cs-user-options").focus(function() {
        	$(".cs-custom-schoollist-btn").removeClass("open");
            $(".sw-dropdown.cs-schoollist-menu").slideUp().attr({
                "aria-hidden": "true"
            });
        });

        // SCHOOL TAB FOCUS
        $(".gb-school-list-tab").focus(function() {
        	// ADJUST TAB ATTRIBUTES
            $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(this).attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");

            // ADJUST PANEL ATTRIBUTES
            $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
            $("#" + $(this).attr("aria-controls")).attr({"aria-hidden": "false"}).removeClass("active");
        });

        // IE TAB CLICK
        $(".ie .gb-school-list-tab span").click(function() {
        	$(this).parent().focus();
        });	

        // SCHOOL TAB KEYDOWN
        $(".gb-school-list-tab").keydown(function(e) {
        	handleTabListKey($(this), ".gb-school-list-tab", e);
        });

        // CLOSE MENU WITH ESC
        $(".gb-school-list-tab, .gb-school-list-panel").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.esc) {
            	e.preventDefault();

                /*$("#gb-schoollist-menu-outer").slideUp(function() {
                    // RESET ATTRIBUTES
                    $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                    $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
                    $("global-icon.schools").focus();
                }).attr("aria-hidden", "true");*/
                if($(".cs-custom-schoollist-btn").attr("aria-expanded") == "false") {
            // MAKE SURE ALL ATTRIBUTES ARE RESET
            $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");

            // SET ELEMENTARY TAB AND PANEL AS ACTIVE
            $(".gb-school-list-tab:first").attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");
            $(".gb-school-list-panel:first").attr({"aria-hidden": "false"}).addClass("active");

            $("#cs-custom-schoollist").stop(true, true).slideDown(function() {
                $(".gb-school-list-tab:first").focus();
                $(".cs-custom-schoollist-btn").attr({
                    "aria-expanded": "true"
                });
            }).attr("aria-hidden", "false");

        }

        // MENU IS OPEN
        else {
            // CLOSE THE MENU
            $("#cs-custom-schoollist").stop(true, true).slideUp(function() {
                // RESET ATTRIBUTES
                $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
                $(".cs-custom-schoollist-btn").attr({
                    "aria-expanded": "false"
                });

            }).attr("aria-hidden", "true");
        }

                $(".cs-custom-schoollist-btn").attr("aria-expanded", "false").focus();
            }
        });

        // SCHOOL MENU LINK KEYDOWN
        $(".gb-school-list-panel a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                	e.preventDefault();

                    // FOCUS THE PREVIOUS ITEM
                    if ($(this).parent().is(":first-child")) { 
                        $(this).parent().parent().find("li:last-child a").focus();
                    } else { 
                        $(this).parent().prev("li").find("a").focus();
                    } 
                break;
                case csKeyCodes.up:
                    e.preventDefault();

                    // FOCUS THE ABOVE ITEM
                    if ($(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) - 3) + ")").length) { 
                        $(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) - 3) + ") a").focus();
                    } else { 
                        return false;
                    } 
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                	// FOCUS THE NEXT ITEM
                    if ($(this).parent().is(":last-child")) { 
                        $(this).parent().parent().find("li:first-child a").focus();
                    } else { 
                        $(this).parent().next("li").find("a").focus();
                    } 
                break;
                case csKeyCodes.down:
                    e.preventDefault();

                    // FOCUS THE BELOW ITEM
                    if ($(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) + 3) + ")").length) { 
                        $(this).parent().parent().find("li:nth-child(" + (($(this).parent().index() + 1) + 3) + ") a").focus();
                    } else { 
                        return false;
                    } 
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS THE FIRST ITEM
                    $(this).parent().parent().find("li:first-child a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS THE LAST ITEM
                    $(this).parent().parent().find("li:last-child a").focus();
                break;

                case csKeyCodes.tab:
                	e.preventDefault();
                    //if ($(this).parent().is(":last-child")) { 
                    	$(".sw-mystart-dropdown.translate .selector").focus();
                        $(".cs-custom-schoollist-btn").removeClass("open");
                        $(".sw-dropdown.cs-schoollist-menu").slideUp().attr({
                            "aria-hidden": "true"
                        });
                    //}
                break;
            }
        });

        $(".cs-close-school-menu").keydown(function(e) {
        	switch(e.keyCode){
                case csKeyCodes.tab:
                	e.preventDefault();
                    $(".cs-custom-schoollist-btn").removeClass("open");
                    $(".sw-dropdown.cs-schoollist-menu").slideUp().attr({
                        "aria-hidden": "true"
                    });
                break;
            }
        });
    }

    function handleTabListKey(tab, tabClass, event) {
    	var lastIndex = $(tabClass).length - 1;

        switch(event.keyCode){
        	// CONSUME LEFT AND UP ARROWS
            case csKeyCodes.left:
            case csKeyCodes.up:
            	event.preventDefault();

                // FOCUS THE PREVIOUS TAB
                if (tab.index() == 0) { 
                    $(tabClass + ":last-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() - 1).focus();
                } 
            break;

            // CONSUME RIGHT AND DOWN ARROWS
            case csKeyCodes.right:
            case csKeyCodes.down:
            	event.preventDefault();

                // FOCUS THE NEXT TAB
                if (tab.index() == lastIndex) { 
                    $(tabClass + ":first-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() + 1).focus();
                }
            break;

            // CONSUME HOME KEY
            case csKeyCodes.home:
            	event.preventDefault();

                // FOCUS THE FIRST TAB
                $(tabClass + ":first-child").focus();
            break;

            // CONSUME END KEY
            case csKeyCodes.end:
            	event.preventDefault();

                // FOCUS THE LAST TAB
                $(tabClass + ":last-child").focus();
            break;
        }
    }

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":

			break;
			case "640":

			break;
			case "480":

			break;
			case "320":

			break;
		}
	}

    function appView() {
    	if($(".sp").length) {
          //DOC dont add app into accordion if it doesnt have a header
          $(".sp.column.one div.ui-widget.app").each(function() {
              $('.ui-widget-header h1', this).append('');
          });

          //DOC accordion header click
          $(".sp.column.one div.ui-widget-header").off('click').click(function(){
              var clickedApp = $(this).parent();

              if($(clickedApp).hasClass('active')){
                  $(clickedApp).removeClass('active');
                  $('.accordion-handle', clickedApp).removeClass('rotate-arrow').addClass('rotate-arrow');
              }else{
                  $("#hp-content div.ui-widget.app.active .accordion-handle").removeClass('rotate-arrow').addClass('rotate-arrow');
                  $("#hp-content div.ui-widget.app.active").removeClass("active");

                  $('.accordion-handle', clickedApp).removeClass('rotate-arrow').addClass('rotate-arrow');
                  $(this).parent().addClass("active");
              }

              smoothScroll("#" + $(clickedApp).parent().attr("id"), 10);
          });
        }
    }

    function searchLightbox() {
    	// SEARCH BUTTON KEYDOWN
        $(document).on("click keydown", "#gb-search-btn", function(e) {
        	// SEARCH BUTTON INVOKED
            if(e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();

                if($(this).attr("aria-expanded") == "true") {
                	$(this).attr({"aria-expanded": "false", "aria-label": "Open search dialog window"});
                	$("#gb-search-cont-outer").removeClass("open").attr("aria-hidden", "true");
                    $("body").css("overflow", "visible");
                } else {
                	$(this).attr({"aria-expanded": "true", "aria-label": "Close search dialog window"});
                	$("#gb-search-cont-outer").addClass("open").attr("aria-hidden", "false");
                    $("body").css("overflow", "hidden");
                    setTimeout(function() {
                    	$("#gb-search-input").focus();
                    }, 400);
                }
            }

            if(e.type == "click") {
                e.preventDefault();

                if($(this).attr("aria-expanded") == "true") {
                	$(this).attr({"aria-expanded": "false", "aria-label": "Open search dialog window"});
                	$("#gb-search-cont-outer").removeClass("open").attr("aria-hidden", "true");
                    $("body").css("overflow", "visible");
                } else {
                	$(this).attr({"aria-expanded": "true", "aria-label": "Close search dialog window"});
                	$("#gb-search-cont-outer").addClass("open").attr("aria-hidden", "false");
                    $("body").css("overflow", "hidden");
                    setTimeout(function() {
                    	//$("#gb-search-input").focus();
                    }, 400);
                }
            }

            // TAB FROM THE SEARCH BUTTON
            if(e.type == "keydown" && e.keyCode == csKeyCodes.tab) {
            	if($(this).attr("aria-expanded") == "true") {
                    e.preventDefault();

                    $("#gb-search-input").focus();
                }
            }

            // SHIFT+TAB FROM THE SEARCH BUTTON
            if(e.type == "keydown" && e.shiftKey && e.keyCode == csKeyCodes.tab) {
            	if($(this).attr("aria-expanded") == "true") {
                	e.preventDefault();

                	$(".sw-mystart-dropdown.translate").focus();
                }
            }
        });

        // SUBMIT SEARCH BUTTON INVOKED
        $(document).on("click keydown", "#gb-submit-search-button", function(e){
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
            	e.preventDefault();
            	var searchForm = $(this).parent();
            	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input", searchForm).val();
        	}
            if(e.type == "keydown" && e.keyCode == csKeyCodes.esc) {
                e.preventDefault();

                // CLOSE THE MENU AND FOCUS THE TOGGLE BUTTON
                $("#gb-search-btn").attr({"aria-expanded": "false", "aria-label": "Open search dialog window"}).focus();
                $("#gb-search-cont-outer").removeClass("open").attr("aria-hidden", "true");
                $("body").css("overflow", "visible");
			}
        });
        $("#gb-search-form").submit(function(e){
            e.preventDefault();
            var searchForm = $(this).parent();
            window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input", searchForm).val();
        });

        //SEARCH TEXT
        $("#gb-search-input").focus(function() {
        	if($(this).val() == "Search") {
        		$(this).val("");
        	}
        }).blur(function() {
        	if($(this).val() == "") {
        		$(this).val("Search");
        	}
        });

        // SEARCH INPUT KEYDOWN
        $("#gb-search-input").keydown(function(e) {
        	// SHIFT+TAB
            if(e.shiftKey && e.keyCode == csKeyCodes.tab) {
                e.preventDefault();

				$("#gb-search-btn").focus();
            }
            // ESC AND CLOSE MENU
            if(e.keyCode == csKeyCodes.esc) {
                    e.preventDefault();

                    // CLOSE THE MENU AND FOCUS THE TOGGLE BUTTON
                    $("#gb-search-btn").attr({"aria-expanded": "false", "aria-label": "Open search dialog window"}).focus();
                	$("#gb-search-cont-outer").removeClass("open").attr("aria-hidden", "true");
                    $("body").css("overflow", "visible");
			}
        });

        // FOCUS LEAVES SEARCH DIALOG
        $(document).keydown(function(e) {
        	// GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
            setTimeout(function() {
            	// IF THE SEARCH INPUT OR BUTTON DO NOT HAVE FOCUS
                if($("#gb-search-cont-outer[aria-hidden='false']").length) {
                    if(!$("#gb-search-cont-outer").find(":focus").length && !$("#gb-search-btn").is(":focus")) {
                        // CLOSE THE MENU AND FOCUS THE TOGGLE BUTTON
                    	$("#gb-search-btn").focus();
                    }
                }
            }, 100);
        });
	}

