            $(".ui-article-description", this).slideDown(250);
            $(".custom-teaser-read-more", this).fadeIn();
            $(".ui-article-info", this).addClass("open").animate( {
            	"height": "100%"
            });
            $(".ui-article-description",this).attr("aria-hidden","false");
            //$(".custom-teaser-read-more",this).attr("aria-hidden","false");
