            $("a.custom-teaser-read-more").attr("tabindex","-1");
        });

        $(".hp-content.top div.ui-widget.app.headlines li, .hp-content.bottom div.ui-widget.app.headlines li").on('focusout', function() {
            var $elem = $(this);
            var autoHeight = $(".ui-article-title", this).height() + 18;
            setTimeout(function() {
                var hasFocus = !! ($elem.find(':focus').length > 0);
                if (!hasFocus) {
                    $elem.removeClass("focused");
                    $(".ui-article-description", this).slideUp(250);
                    $(".custom-teaser-read-more", this).fadeOut("fast");
                    $(".ui-article-info", this).removeClass("open").animate( {
                        "height": autoHeight
                    });
                    $(".ui-article-description",this).attr("aria-hidden","true");
                    //$(".custom-teaser-read-more",this).attr("aria-hidden","true");
                    $("a.custom-teaser-read-more",this).addClass("hover");
                    $("a.custom-teaser-read-more").attr("tabindex","-1");
                }
            }, 10);
        });
    }

    function magicLine() {
		var element; 
		var leftPos;
		var newWidth;
		var mainNav = $("#channel-navigation");
		$("#magic-line").remove();
		mainNav.prepend("
");
		var magicLine = $("#magic-line");

		if($("li.sw-channel-item.active").size()) {
			magicLine.width($("li.sw-channel-item.active").width()).css("left", $("li.sw-channel-item.active").position().left).data("origLeft", magicLine.position().left).data("origWidth", magicLine.width());
		} else if($("li.sw-channel-item.hover-line").size()) {
			magicLine.width($("li.sw-channel-item.hover-line").width()).css("left", $("li.sw-channel-item.hover-line").position().left).data("origLeft", magicLine.position().left).data("origWidth", magicLine.width());
		} else {
			magicLine.width($("li.sw-channel-item").eq(0).width()).css("left", $("li.sw-channel-item").eq(0).position().left).data("origLeft", magicLine.position().left).data("origWidth", magicLine.width());	
		}

		$("li.sw-channel-item").hover(function() {
        	var thisChannel = this;
			$("li.sw-channel-item").removeClass("hover-line");
            $("li.sw-channel-item").removeClass("delayHover");
			$(this).addClass("hover-line");
            leftPos = $(this).position().left;
			newWidth = $(this).width();
			magicLine.stop(true,true).animate({ left: leftPos, width: newWidth }, "fast");
            magicLine.animate({height: "100%"}, "fast");
            $(thisChannel).addClass("delayHover");
		}, function() {
        	leftPos = $("li.sw-channel-item.active").position().left;
        	if($("li.sw-channel-item.hover").hasClass("hover")) {

            } else {
            	newWidth = $(".sw-channel-item.active").width();
                magicLine.animate({height: "6px"}, "fast");
                magicLine.animate({left: leftPos, width: newWidth}, "fast");
                $("li.sw-channel-item").removeClass("hover-line");
                $("li.sw-channel-item").removeClass("delayHover");
            }
		});

	}

