    function csRsMenu(sortedSchools) {
        //ADD SCHOOLS FROM MEGA MENU
    	var headerCounter = 1;

        // BUILD THE SCHOOL MENU STRUCTURE
        var schoolHeaders = "";
        var schoolItems = "";
        $.each(sortedSchools, function(type, schools) {
            schoolHeaders += ((headerCounter > 0) ? ";" : "") + type;
            headerCounter++;

            for(var school in schools) {
            	schoolItems += (headerCounter) + ',' + schools[school].schoolName + ',' + schools[school].schoolUrl + ',_self;';
            }
        });

    	// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"			: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
            "slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		    "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		    "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			"menuButtonParent"		: ".gb-header.left",
			"showMenuBtnText"		: "yes",
			"menuBtnText"			: "MENU",
			"showSchools"			: "no",
			"showTranslate"			: "yes",
			"translateId"			: "none",
            "advancedTranlate": {
            	"useAdvancedList": "yes", // yes = USE THE NEXT TWO OPTIONS BELOW, no = USE THE REGULAR LIST OF 11 LANGUAGES
            },
			"showAccount"			: "yes",
			"extraMenuOptions"		: "District Home" + schoolHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"		: "1,District Home,/,_self;" + schoolItems, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"				: "4"
		});

        if($("#rs-menu-btn").length) {
            $(".gb-header.left").after($("#rs-menu-btn"));
        }
    }

