        $(document).ready(function () {
            SetCookie('SWScreenWidth', screen.width);
            SetCookie('SWClientWidth', document.body.clientWidth);
            
            $("div.ui-article:last").addClass("last-article");
            $("div.region .app:last").addClass("last-app");

            // get on screen alerts
            var isAnyActiveOSA = 'False';
            var onscreenAlertCookie = GetCookie('Alerts');

            if (onscreenAlertCookie == '' || onscreenAlertCookie == undefined) {
                onscreenAlertCookie = "";
            }
            if (isAnyActiveOSA == 'True') {
                GetContent(homeURL + "/cms/Tools/OnScreenAlerts/UserControls/OnScreenAlertDialogListWrapper.aspx?OnScreenAlertCookie=" + onscreenAlertCookie + "&SiteID=1", "onscreenalert-holder", 2, "OnScreenAlertCheckListItem();");
            }

            

        });

    // ADA SKIP NAV
    $(document).ready(function () {
        $(document).on('focus', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "0px"
            }, 500);
        });

        $(document).on('blur', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "-30px"
            }, 500);
        });
    });

    // ADA MYSTART
    $(document).ready(function () {
        var top_level_nav = $('.sw-mystart-nav');

        // Set tabIndex to -1 so that top_level_links can't receive focus until menu is open
        // school dropdown
        $(top_level_nav).find('ul').find('a').attr('tabIndex', -1);

        // my account dropdown
        $(top_level_nav).next('ul').find('a').attr('tabIndex', -1);

        var openNavCallback = function(e, element) {
             // hide open menus
            hideMyStartBarMenu();

            // show school dropdown
            if ($(element).find('ul').length > 0) {
                $(element).find('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).find('.sw-dropdown').find('li:first-child a').focus()
            }

            // show my account dropdown
            if ($(element).next('ul').length > 0) {
                $(element).next('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).next('.sw-dropdown').find('li:first-child a').focus();
                $('#sw-mystart-account').addClass("clicked-state");
            }
        }

        $(top_level_nav).click(function (e) {
            openNavCallback(e, this);
        });

        $('.sw-dropdown-list li').click(function(e) {
            e.stopImmediatePropagation();
            $(this).focus();
        });
        
        // Bind arrow keys for navigation
        $(top_level_nav).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).prev('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').last().focus();
                } else {
                    $(this).prev('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }

                // show my account dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).next('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').first().focus();
                } else {
                    $(this).next('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }

                // show my account dropdown
                if ($(this).next('ul').length > 0) {
                    $(this).next('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                
                openNavCallback(e, this);
                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideMyStartBarMenu();
            } else {
                $(this).parent('.sw-mystart-nav').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        // school dropdown
        var startbarlinks = $(top_level_nav).find('ul').find('a');
        bindMyStartBarLinks(startbarlinks);

        // my account dropdown
        var myaccountlinks = $(top_level_nav).next('ul').find('a');
        bindMyStartBarLinks(myaccountlinks);

        function bindMyStartBarLinks(links) {
            $(links).keydown(function (e) {
                e.stopPropagation();

                if (e.keyCode == 38) { //key up
                    e.preventDefault();

                    // This is the first item
                    if ($(this).parent('li').prev('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').prev('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 40) { //key down
                    e.preventDefault();

                    if ($(this).parent('li').next('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').next('li').find('a').first().attr('tabIndex', 0);
                        $(this).parent('li').next('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 27 || e.keyCode == 37) { // escape key or key left
                    e.preventDefault();
                    hideMyStartBarMenu();
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;

                    $(this).parent('div').nextAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            found = true;
                            return false;
                        }
                    });

                    if (!found) {
                        $(this).parent('div').prevAll('li').find('a').each(function () {
                            if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                                $(this).focus();
                                return false;
                            }
                        });
                    }
                }
            });
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('#sw-mystart-inner').find('.sw-mystart-nav').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() { 
            hideMyStartBarMenu();
        });*/

        // try to capture as many custom MyStart bars as possible
        $('.sw-mystart-button').find('a').focus(function () {
            hideMyStartBarMenu();
        });

        $('#sw-mystart-inner').click(function (e) {
            e.stopPropagation();
        });

        $('ul.sw-dropdown-list').blur(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-mypasskey').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-sitemanager').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-myview').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-signin').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-register').focus(function () {
            hideMyStartBarMenu();
        });

        // button click events
        $('div.sw-mystart-button.home a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.pw a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.manage a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#sw-mystart-account').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).addClass('clicked-state');
                $('#sw-myaccount-list').show();
            }
        });

        $('#sw-mystart-mypasskey a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.signin a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.register a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });
    });

    function hideMyStartBarMenu() {
        $('.sw-dropdown').attr('aria-hidden', 'true').css('display', 'none');
        $('#sw-mystart-account').removeClass("clicked-state");
    }

    // ADA CHANNEL NAV
    $(document).ready(function() {
        var channelCount;
        var channelIndex = 1;
        var settings = {
            menuHoverClass: 'hover'
        };

        // Add ARIA roles to menubar and menu items
        $('[id="channel-navigation"]').attr('role', 'menubar').find('li a').attr('role', 'menuitem').attr('tabindex', '0');

        var top_level_links = $('[id="channel-navigation"]').find('> li > a');
        channelCount = $(top_level_links).length;


        $(top_level_links).each(function() {
            $(this).attr('aria-posinset', channelIndex).attr('aria-setsize', channelCount);
            $(this).next('ul').attr({ 'aria-hidden': 'true', 'role': 'menu' });

            if ($(this).parent('li.sw-channel-item').children('ul').length > 0) {
                $(this).attr('aria-haspopup', 'true');
            }

            var sectionCount = $(this).next('ul').find('a').length;
            var sectionIndex = 1;
            $(this).next('ul').find('a').each(function() {
                $(this).attr('tabIndex', -1).attr('aria-posinset', sectionIndex).attr('aria-setsize', sectionCount);
                sectionIndex++;
            });
            channelIndex++;

        });

        $(top_level_links).focus(function () {
            //hide open menus
            hideChannelMenu();

            if ($(this).parent('li').find('ul').length > 0) {
                $(this).parent('li').addClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'false').css('display', 'block');
            }
        });

        // Bind arrow keys for navigation
        $(top_level_links).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').find('> li').last().find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li').addClass(settings.menuHoverClass).find('ul').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').find('> li').first().find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li')
                         .addClass(settings.menuHoverClass)
                         .find('ul.sw-channel-dropdown').css('display', 'block')
                         .attr('aria-hidden', 'false')
                         .find('a').attr('tabIndex', 0)
                         .first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').addClass(settings.menuHoverClass).find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideChannelMenu();
            } else {
                $(this).parent('li').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        var links = $(top_level_links).parent('li').find('ul').find('a');

        $(links).keydown(function (e) {
            if (e.keyCode == 38) {
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) {
                e.preventDefault();

                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 27 || e.keyCode == 37) {
                e.preventDefault();
                $(this).parents('ul').first().prev('a').focus().parents('ul').first().find('.' + settings.menuHoverClass).removeClass(settings.menuHoverClass);
            } else if (e.keyCode == 32) {
                e.preventDefault();
                window.location = $(this).attr('href');
            } else {
                var found = false;

                $(this).parent('li').nextAll('li').find('a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        found = true;
                        return false;
                    }
                });

                if (!found) {
                    $(this).parent('li').prevAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            return false;
                        }
                    });
                }
            }
        });

        function hideChannelMenu() {
            $('li.sw-channel-item.' + settings.menuHoverClass).removeClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'true').css('display', 'none').find('a').attr('tabIndex', -1);
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('[id="channel-navigation"]').find('a').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideChannelMenu();
            }
        });

        $('[id="channel-navigation"]').find('a').first().keydown(function (e) {
            if (e.keyCode == 9) {
                // hide open MyStart Bar menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() {
            hideChannelMenu();
        });*/

        $('[id="channel-navigation"]').click(function (e) {
            e.stopPropagation();
        });
    });

    $(document).ready(function() {
        $('input.required').each(function() {
            if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                    $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                }
            }
        });

        $(document).ajaxComplete(function() {
            $('input.required').each(function() {
                if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                    if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                        $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                    }
                }
            });
        });
    });

	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

	$(document).ready(function() {
		iconDate();
		activeChannel();
		if ($("#hp-main-container").size()) {
			if($("#sw-content-container2 div.app.announcements").size()){
				$("#sw-content-container2").announceApp();
			}
			if($("#sw-content-container1 div.app.headlines").size()){
				$("#sw-content-container1").hlApp();
			}
			$("#sw-content-container4, #sw-content-container5").addFor();	
		}
		$("#sw-content-container3.ui-hp").tabBlocks();
		modAreas();
		sameHeight();
        accessibilityMods();
        $("#sw-footer-outer").remove();
	});

    function accessibilityMods() {
    	//SEARCH
        $("#sw-mystart-search").unbind();
        $("#sw-search-input").before('Search Input');
        $("#sw-search-button").attr({
        	"role": "button",
            "aria-label": "Search Our Site"
        });
        $("#sw-search-button").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                SWGoToSearchResultsPageswsearchinput();
            }
        });

        //SELECT A SCHOOL DROPDOWN
        $(".sw-mystart-dropdown.schoollist .sw-dropdown li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                    e.preventDefault();

                    	$(".sw-mystart-button.signin a").focus();

                break;
            }
        });

        //MY ACCOUNT DROPDOWN
        $("#sw-mystart-account #sw-myaccount-list li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                    e.preventDefault();
                    	$("#sw-mystart-mypasskey a").focus();
                break;
            }
        });
    }

	function sameHeight(){
		columnsFix();

		$("div.hp.column").resize(function() {
			$("div.hp.column div.ui-widget-detail").height("");
	      		columnsFix();
                });

                function columnsFix(){
                	var parentHeight = $("#hp-columns-inside").height();
                	//var col1Height = parentHeight - $("div.hp.column.one div.ui-widget-detail:eq(" +$("div.tab-button.active").index() + ")").height();
                	$("div.hp.column.one div.ui-widget-detail:eq(" + $("div.tab-button.active").index() + ")").height(parentHeight-89);
                	for(j=1;j<3;j++){
	                	var appsHeight = 0;
	                	var numbersArray = ["one","two","three"];
	                	$("div.hp.column." + numbersArray[j] + " div.ui-widget-detail").each(function(){
                			appsHeight = appsHeight + $(this).height() + 79;
                			var lastAppHeight = parentHeight - (appsHeight - $("div.hp.column." + numbersArray[j] + " div.ui-widget-detail:last").height());
                			$("div.hp.column." + numbersArray[j] + " div.ui-widget.app:last div.ui-widget-detail").height(lastAppHeight);
	                	});
	                }
                }

	}

	function modAreas() {
		$("div.ui-widget.app.pagenavigation div.ui-widget-header").prepend("
");
		$("#sw-mystart-outer").prepend("
");

        $("#hp-main-container #sw-content-container2 .app-level-social-rss").appendTo("#hp-main-container #sw-content-container2 div.ui-widget-header");
	}
	$.fn.addFor = function() {
		$(this).find(".ui-widget-header h1").prepend("For ");
	}
	$.fn.announceApp = function() {
		var myDiv = "#" + $(this).attr("id");
		var totalVal = $(myDiv).find("ul.ui-articles > li").size();
		$(this).find("div.ui-widget-detail").append("") 
		$(this).find("ul.ui-articles > li").each(function() {
			var leftVal = $(this).index() * 220;
			$(this).css({
				position: "absolute",
				top: "0px",
				left: leftVal
			});
            if(leftVal == "0") {
            	$(this).attr({
                	"aria-hidden": "false"
                });
                $("a",this).attr("tabindex","0");
            } else {
            	$(this).attr({
                	"aria-hidden": "true"
                });
                $("a",this).attr("tabindex","-1");
            }
			$("ul.app-nav").append("
" + ($(this).index() + 1) + "");
		});
		$("ul.app-nav li:eq(0)").addClass("active");
		$("ul.app-nav li").click(function() {
			$("ul.app-nav li").removeClass("active");
			$(this).addClass("active");
			var myVal = $(this).index();
			announceSlide(myVal, totalVal, myDiv);
		});
        $("ul.app-nav li").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                $("ul.app-nav li").removeClass("active");
                $(this).addClass("active");
                var myVal = $(this).index();
                announceSlide(myVal, totalVal, myDiv);
            }
            if(e.keyCode == csKeyCodes.left) {
            	e.preventDefault();
                $(this).prev().focus();
            }
            if(e.keyCode == csKeyCodes.right) {
            	e.preventDefault();
                $(this).next().focus();
            }
        });
		announceAutoRotate(totalVal, myDiv);
		$(this).hover(function() {
			clearInterval(annTimer);
		}, function() {
			announceAutoRotate(totalVal, myDiv)
		});
	}

	function announceSlide(myVal, totalVal, myDiv) {
		var mySlideLeftVal = parseInt($(myDiv).find("ul.ui-articles > li").eq(myVal).css("left").replace("px", ""));
		$(myDiv).find("ul.ui-articles > li").each(function() {
			var moveTo = parseInt($(this).css("left").replace("px", "")) - mySlideLeftVal;
			$(this).animate({
				left: moveTo
			}, 500, function() { 
				// Animation complete.   
			});  
            if(moveTo == "0") {
            	$(this).attr({
                	"aria-hidden": "false"
                });
                $("a",this).attr("tabindex","0");
            } else {
            	$(this).attr({
                	"aria-hidden": "true"
                });
                $("a",this).attr("tabindex","-1");
            }
		}); 
	}
	function announceAutoRotate(totalVal, myDiv){
		annTimer = setInterval(function(){    
			var myVal = $(myDiv).find("ul.app-nav li.active").index();
			if((totalVal-1) == myVal){     
				var nextVal = 0;    
			} else{    
				var nextVal = myVal+1;
			}               
			$(myDiv).find("ul.app-nav li").removeClass("active");
			$(myDiv).find("ul.app-nav li").eq(nextVal).addClass("active");
			announceSlide(nextVal, totalVal, myDiv)}, 5000);
	}  

	function activeChannel(){  
		$("li.sw-channel-item.active").append("
");
	}  
	function iconDate() {          
		//THIS IS FOR A DATE ICON  
		var currentDate = new Date();  
		var currDay = currentDate.getDate();  
		$("a.global:last").append("
" + currDay + ""); 
	}     
	$.fn.hlApp = function(){  
		$(this).children("div:eq(0)").hide();  
		animating = false;  
		var myDiv = "#"+$(this).attr("id");    
		var headlineArray = buildArray(myDiv);    
		buildStructure(myDiv, headlineArray);    
		hlAutoRotate(myDiv, headlineArray);    
		$(this).hover(function () {        
			clearInterval(hlTimer);     
		}, function () {        
			hlAutoRotate(myDiv, headlineArray)     
		}); 
	}  
	function buildArray(myDiv){  
		//TAG for easy access  
		$(myDiv).find("ul.ui-articles:eq(0)").addClass("processed");  
		//COUNT number of headlines  
		var hlcount = $("ul.processed li").size();   
		var headlinesArray = new Array();    
		for(j=0;j<hlcount;j++){   
			headlinesArray[j] = new Array();   
			//Sets the multidimensional array values   
			var currentHeadline = "ul.processed li:eq(" + j + ")";   
			var currentHeadlineLink = currentHeadline + " a:eq(0)";   
			//var currentHeadlineImg = currentHeadline + " a:eq(0) img"; 
            var currentHeadlineImg = currentHeadline + " .ui-article-thumb img";  /* changed from a:eq(0) img by Matt B - case#3928332 - 08.10.18 */
			var currentHeadlineTitle = currentHeadline + " h1 a";   
			var currentHeadlineTeaser = currentHeadline + " p";   
			headlinesArray[j][0] = $.trim($(currentHeadlineTitle).text());   
			headlinesArray[j][1] = $.trim($(currentHeadlineTeaser).html());   
			headlinesArray[j][2] = $(currentHeadlineImg).attr("src");   
			headlinesArray[j][3] = $(currentHeadlineLink).attr("href"); 
            headlinesArray[j][4] = $(currentHeadlineImg).attr("alt"); 
		}  
		return headlinesArray; 
	}  
	function buildStructure(myDiv, hlArray){  
		var structure = "
" +    
				" 
" + hlArray[0][0] + " " +          
				" " + 
				" " +          
				" 
" +          
				"  " +          
				"   " +          
				"  " +          
				" " +          
				" 
" +                                       
				" " +          
				"";  
		$(myDiv).append(structure); 
		if(hlArray[0][1] != ""){
			$("div.hlApp-desc-container").append("
" + hlArray[0][1] + " Read More...about this article");
		} 
		//BUILD NAVIGATION  
		for(j=0;j<hlArray.length;j++){   
			$(myDiv).find(".hlApp-nav").append("
" + (j+1) + "");  
		}  
		$("ul.hlApp-nav li:eq(0)").addClass("active");  
		//SET CLICK FUNCTIONS  
		$("ul.hlApp-nav li").click(function(){          
			$("ul.hlApp-nav li").removeClass("active");          
			$(this).addClass("active");          
			var myVal = $(this).index();       
			fadeSwap(myDiv, myVal, hlArray);      
		}); 
        $("ul.hlApp-nav li").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                $("ul.hlApp-nav li").removeClass("active");          
                $(this).addClass("active");          
                var myVal = $(this).index();       
                fadeSwap(myDiv, myVal, hlArray);
            }
            if(e.keyCode == csKeyCodes.left) {
            	e.preventDefault();
                $(this).prev().focus();
            }
            if(e.keyCode == csKeyCodes.right) {
            	e.preventDefault();
                $(this).next().focus();
            }
        });
	}  
	function fadeSwap(myDiv, myVal, hlArray){  
		//SET IMAGE  
		var nextImg = hlArray[myVal][2];  
        var nextImgTitle = hlArray[myVal][4];
		$(myDiv).find(".hlApp-rotate a").prepend("");  
		$(myDiv).find(".hlApp-rotate img:eq(1)").fadeOut('slow', function() {       
			$(this).remove();    
		});   
		var myHeight = "-" + $(".desc-detail").height();  
		$(myDiv).find(".desc-detail").animate({
			bottom: myHeight,          
			opacity: "0"                 
		}, 200, function(){
			if(hlArray[myVal][1] != ""){
				var htmlDesc = hlArray[myVal][1] + " Read More...about this article";                  
				$(myDiv).find(".desc-detail").html(htmlDesc);                  
				$(this).animate({bottom:"0px", opacity:".9"},200);
			}                
		});  
		//SET TEXT  
		$(myDiv).find(".hlApp-title").text(hlArray[myVal][0]);  
		//SET LINK  
		$(myDiv).find(".hlApp-rotate a").attr("href",hlArray[myVal][3]); 
	}  
	function hlAutoRotate(myDiv, hlArray){   
		hlTimer = setInterval(function(){    
		var totalVal = $("ul.hlApp-nav li").size();    
		if((totalVal-1) == $("ul.hlApp-nav li.active").index()){     
			var nextVal = 0;    
		} else{    
			var nextVal = $("ul.hlApp-nav li.active").index()+1;   
		}               
		$("ul.hlApp-nav li").removeClass("active");               
		$("ul.hlApp-nav li").eq(nextVal).addClass("active");     
		fadeSwap(myDiv, nextVal, hlArray)}, 6000); 
	}

