jQuery(window).scroll(function(e){ 

  var stop = jQuery('.cbp-row.wh-footer').offset().top;
  var stop_on = stop - 600;
  var start_on = stop - 750;

  var $el = jQuery('.wh-sidebar'); 
  var isPositionFixed = ($el.css('position') == 'fixed');
  if (jQuery(this).scrollTop() > 750 && jQuery(this).scrollTop() < stop_on && !isPositionFixed){ 
    $el.css({'position': 'fixed', 'top': '100px'}); 
  }
  if (jQuery(this).scrollTop() > stop_on && isPositionFixed){ 
    $el.css({'position': 'absolute', 'top': start_on }); 
  }
  if (jQuery(this).scrollTop() < 750 && isPositionFixed){
    $el.css({'position': 'absolute', 'top': 'unset'}); 
  } 
});

