$(document).ready(function () {
    $("#31422960-6a45-47f5-a2b3-5a08a64a98f5").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 10 > 1 ? autoPlayValue : false;
    }
});

