    	kLeft = 37, //-- Key Left
        kUp = 38, //-- Key Up
        kRight = 39, //-- Key Right
        kDown = 40, //-- Key Down
        kEnter = 13, //-- Key Enter
        kShift = 16, //-- Key Shift
        kSpace = 32, //-- Key Space
        kEsc = 27, //-- Key Escape
        kBack = 8, //-- Key Backspace/Delete
        k1 = 49, //-- Key 1
        k2 = 50,
        k3 = 51,
        k4 = 52,
        k5 = 53,
        k6 = 54,
        k7 = 55,
        closeSVG = '',
        menuSVG = '',
        rssSVG = '',
        arrowSVG = '',
        plusSVG = 'Asset 1',
        gearSVG = '',
        searchSVG = '',
        translateSVG = '';

