	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE7){ $("body").addClass("msie7"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }
		if(isMSIE11){ $("body").addClass("msie11"); }
		$("body").addClass(navigator.platform + " " + browserName + " " + browserName + browserVersion);

		$(window).bind("resize", function(){ 
			jsMediaQueries(); 
            resizes();
		});

		// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"          : "#003e78", // DEFAULT - #1A1A1A
		        "secondaryColor"        : "#00274c", // DEFAULT - #0C0C0C
		        "accentColor"           : "#ffcb0a", // DEFAULT - #BABABA
			"menuButtonParent"	: "#rs-menu-container",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: '',
			"showSchools"		: "yes",
			"showTranslate"		: "yes",
			"translateId"		: "none",
			"showAccount"		: "yes",
			"extraMenuOptions"	: "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
			"breakPoint"		: 0, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		        "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		        "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			"menuButtonParent"	: "#sp-content",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "PAGES",
			"siteID"			: "4"
		});

		jsMediaQueries();
		useChannelForSection();
		dynamicContent();

        streamingVideo();
        if(!(true)){
			mediaRotator();
        }
	});	


    function loaded(){


        //-- Apply Bullets
        $('.hp-upper-content .ui-widget.app.siteshortcuts .site-shortcuts > li.expandable, .hp-upper-content .ui-widget.app.siteshortcuts .site-shortcuts > li.collapsible').each(function(k, v){
        	$(' > .bullet', v).append(arrowSVG);
        });

        $('.navigation ul > li.expandable, .navigation ul > li.collapsible').each(function(k, v){
        	if($('.arrow-icon', v).size() == 0){
            	$('> .bullet', v).append(plusSVG);
            }
        });

        $('.flexpage .ui-article ul li, .flexpage .ui-article ol li').each(function(k, v){
        	$(v).html('' + $(v).html() + '');
        });

        //-- Check Twitter Toggle
        var tFToggle = 'true',
        	tFLink = 'https://twitter.com/muhlsd?ref_src=twsrc%5Etfw',
            tIcon = 'Brid',
            tFHTML = '' + tIcon + '';

        if(tFToggle == 'true'){
        	$('.hp.column.three .ui-widget-header').prepend(tFHTML);
        }

    }
    function resizes(){

    	var vpw = $(window).width();
        if(vpw < 1024){
        	$('#calendar-btn-change-month').click();
        	if($('#rs-menu-btn .menu-icon').size() == 0){
            	$('#rs-menu-btn').html(menuSVG);
            }
            $('.hp.column.two .ui-widget.app, .hp.column.three .ui-widget.app, #hp-content-lower .ui-widget.app').each(function(k, v){
            	if($('.close-icon', this).size() == 0){
                	$('.ui-widget-header', this).append(closeSVG);
                }
            });
            $('.gb-footer-district-inner').append($('#gb-to-top'));
            $('#gb-global-icons').removeClass('cs-fcc cs-jc').addClass('cs-fc cs-jsa');
            $('#sw-mystart-mypasskey').appendTo($('.gb.footer.one'));
            if($('.sp.column.one .plus-icon').size() != 0){
                $('.sp.column.one .plus-icon').remove();
            }
            if(vpw < 768){
            	$('#gb-map').insertBefore($('.gb-footer-contact-container'));
                if($('.sp.column.one .ui-widget-header .plus-icon').size() == 0){
                	$('.sp.column.one .ui-widget-header').append(closeSVG);
                    $('.sp.column.one .ui-widget-detail').slideUp();
                }
                if(vpw < 640){
                	$('#ui-btn-calendarlist-list').click();
                    if(vpw < 480){
                        if($('#sw-mystart-mypasskey').size() == 0){
                            $('.gb.footer.one').append($('#gb-map'));
                        } else {
                            $('#gb-map').insertBefore($('#sw-mystart-mypasskey'));
                        }
                    }
                }
            }
        } else {
        	$('#calendar-btn-change-month').click();
        	$('.close-icon').remove();
            $('#cs-sw-footer').append($('#gb-to-top'));
            $('#gb-global-icons').addClass('cs-fcc cs-jc').removeClass('cs-fc cs-jsa');
            $('.gb-custom-mystart-right').prepend($('#sw-mystart-mypasskey'));
        }
    }

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				appViewReset();

			break;
			case "768":
				appViewReset();
			break;
			case "640":
				appViewReset();
			break;
			case "480":
				appView();
			break;
			case "320":
				appView();
			break;
		}
	}

	function mediaRotator(){

        //$('head').prepend('');
        //-- Since script loading is dynamic/async, we take
        //-- a callback function with our loadScript call
        //-- that executes once the script is done downloading/parsing
        //-- on the page.

        var loadScript = function(src, callbackfn) {
            var newScript = document.createElement("script");
            newScript.type = "text/javascript";
            newScript.setAttribute("async", "true");
            newScript.setAttribute("src", src);

            if(newScript.readyState) {
                newScript.onreadystatechange = function() {
                    if(/loaded|complete/.test(newScript.readyState)) callbackfn();
                }
            } else {
                newScript.addEventListener("load", callbackfn, false);
            }

            document.documentElement.firstChild.appendChild(newScript);
        }

