            	e.preventDefault();
                if(c == kDown){
                	next.focus();
                } else if(c == kUp && $(this).hasClass('first')){
                	$(this).closest('.gb-custom-dropdown').focus();
                    $('.gb-custom-dropdown').removeClass('inner');
                    $('.sw-dropdown a').attr('tabindex', '-1');
