        					['true','Muhlenberg Township','http://www.muhlenbergtwp.com','_blank'],
                            ['true','Laureldale Boro','http://www.laureldaleboro.org','_blank']
                            ];
        for(var i in addLinkArray){
        	if(addLinkArray[i][0] == 'true'){
            	var link = '' + addLinkArray[i][1] + '  •  ';
            	$('.gb.footer.two').prepend(link);
            }
        }
	}

    function streamingVideo() {
    	if(true && $(".hp").length) {
				var options = {
                    id: 323846174,
                    title: false,
                    byline: false,
                    autoplay: true,
                    loop: true
                };

                var player = new Vimeo.Player("hp-video-content", options);

                player.ready().then(function() {
                        player.setVolume(1);

                        Promise.all([player.getVideoWidth(), player.getVideoHeight()]).then(function(dimensions) {
                            var videoWidth = dimensions[0];
                            var videoHeight = dimensions[1];

                            videoResize(videoWidth, videoHeight);
                            $(window).resize(function() { videoResize(videoWidth, videoHeight); });
                    });

                }); 
        }
	}

    function videoResize(videoWidth, videoHeight) {
        var videoRatio = videoHeight / videoWidth;
        var screenWidth = $(window).width();
        var screenHeight = $(window).height();

        var adjustedVideoWidth = screenWidth;
        var adjustedVideoHeight = adjustedVideoWidth * videoRatio;

        //console.log("screen width: " + screenWidth + ", screen height: " + screenHeight + ", video ratio: " + videoRatio);

        if(adjustedVideoHeight < screenHeight) {
                adjustedVideoHeight = screenHeight;
                adjustedVideoWidth = adjustedVideoHeight / videoRatio;
        }

        var centerWidth = (screenWidth - adjustedVideoWidth) / 2;
        var centerHeight = (screenHeight - adjustedVideoHeight) / 2;

        if(screenWidth >= 1024){
            $("#hp-video-content iframe").css({
                "width": adjustedVideoWidth,
                "height": adjustedVideoHeight,
                "margin-top": centerHeight * 2,
                "margin-left": centerWidth
            });
        } else {
        	$("#hp-video-content iframe").css({
                    "width": screenWidth,
                    "height": screenWidth * videoRatio,
                    "margin-top": centerHeight * 2,
                    "margin-left": "0px"
                });
        }
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
            $('.ui-article-title', this).append($('.ui-article-title .joel-month', this));
		});
        $(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).append('ALL DAY');
				} else {
                	$(this).append($('.sw-calendar-block-time', this));
                }
			});
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("input#sw-search-input").removeAttr("placeholder");
		$("input#sw-search-input").attr("value", "Search...");
		$("input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function calendarListView() {
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}


	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	};

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	};

