}

link.script:
$(document).ready(function () {
    $(".GoogleSearch").css("display", "block");
    var h = $(".GoogleSearch").outerHeight();
    $(".GoogleSearch").css("margin-top", -h + "px");
    setTimeout(function () {
        $(".GoogleSearch").css("display", "block");
        var h = $(".GoogleSearch").outerHeight();
        $(".GoogleSearch").css("margin-top", -h + "px");
        setTimeout(function () {
            $(".GoogleSearch").css("transition", "all 0.5s");
        }, 20);
    }, 500);
});
function searchClick() {
    $(".GoogleSearch").css("margin-top", "0px");
}
function searchClose() {
    var h = $(".GoogleSearch").outerHeight();
    $(".GoogleSearch").css("margin-top", -h + "px");
}

function translateMover() {
    if (window.innerWidth < 991)
        $(".quicklinks").appendTo(".slicknav_nav");
    else
        $(".quicklinks").appendTo("header .container-fluid");
}
function sideNavMover() {
    if (window.innerWidth < 991)
        $(".SideNavLink").appendTo(".slicknav_nav");
    else
        $(".SideNavLink").appendTo(".SideNav");
}
$(document).ready(function () {
    sideNavMover();
    translateMover();
});
$(window).resize(function () {
    sideNavMover();
    translateMover();
});

div.script:
function popUp(URL) {
        day = new Date();
        id = day.getTime();
        eval("page" + id + " = window.open(URL, '" + id + "', 'toolbar=0,scrollbars=1,location=0,statusbar=1,menubar=0,resizable=1,width=700,height=600,left = 440,top = 212');");
    }

