        // Toggle function for FAQ liks
        $(".faq-question").click(function (e) {
            e.preventDefault();
            if ($(this).next(".faq-answer").css("display") == "block") {
                $(this).next(".faq-answer").hide();
            } else {
                $(".faq .faq-answer").each(function () {
                    $(this).hide();
                });
                $(this).next(".faq-answer").toggle();
            }
        });


        // ::::::::::::::::::::::::::: //
        // Search form functions
        // ::::::::::::::::::::::::::: //

        // Set search input placeholder to correct searchtype

        $("label[data-placeholder^=Search]").click(function () {
            $("#searchText").attr("placeholder", $(this).data("placeholder"));
        });

        // ::::::::::::::::::::::::::: //
        // Footer button functions
        // ::::::::::::::::::::::::::: //
        function clearPopups() {
            $('.footer-popup').hide();
            $('.footer-popup').removeClass("open");
        }

        $(".footer-icon").click(function () {
            var thePopup = $(this).find(".footer-popup");
            if (thePopup.hasClass("open")) {
                thePopup.hide();
                thePopup.removeClass("open");
            } else {
                clearPopups();
                thePopup.show();
                thePopup.addClass("open");
            }
        });

