meta.script:
 function defer(method) {
    if (window.jQuery) {
        method();
    } else {
        setTimeout(function() { defer(method) }, 50);
    }
}
function gFormDefer(method) {
	// console.log(gformInitSpinner);
    if (window.jQuery && window.gformInitSpinner) {
        method();
    } else {
        setTimeout(function() { gFormDefer(method) }, 50);
    }
}

