        "rotatingHeadlines": function(){
        	// FOR SCOPE
            var template = this;

            $("#foreground-top div.ui-widget.app.headlines .ui-widget-footer").append("Learn More");

            rotateHeadlines();

            function rotateHeadlines(){
            	$("#foreground-top div.ui-widget.app.headlines").hide();
                $("#foreground-top div.ui-widget.app.headlines li").attr("aria-hidden","true");

				$("#foreground-top div.ui-widget.app.headlines").eq(0).show();
				$("#foreground-top div.ui-widget.app.headlines").eq(0).each(function() {
					if(!$(".active", this).size()) {
						var index = 0;
						$("li", this).eq(index).addClass("active").attr("aria-hidden","false");
						$(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
						var moreInfoURL = $("li.active .ui-article-title a", this).attr("href");
						var headlineTitle = $("li.active .ui-article-title a", this).text();
						$("#more-info").attr({
							"href" : moreInfoURL,
							"aria-label" : "Learn More about "+headlineTitle
						});
					} else {
						var index = $(".active", this).index();
						$(".active", this).animate({ opacity: 0 }, 1500, function() { }); 
						$(".active", this).attr("aria-hidden","true").removeClass("active");
						if(index == $("li", this).length-1) {
							$("li", this).eq(0).addClass("active");
							var moreInfoURL = $("li.active .ui-article-title a", this).attr("href");
							var headlineTitle = $("li.active .ui-article-title a", this).text();
							$("#more-info").attr({
								"href" : moreInfoURL,
								"aria-label" : "Learn More about "+headlineTitle
							});
						}
						else {
							$("li", this).eq(index).next("li").addClass("active").attr("aria-hidden","false");
							var moreInfoURL = $("li.active .ui-article-title a", this).attr("href");
							var headlineTitle = $("li.active .ui-article-title a", this).text();
							$("#more-info").attr({
								"href" : moreInfoURL,
								"aria-label" : "Learn More about "+headlineTitle
							});
						}
						$(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
					}
				});	
            }
        },

        "modEvents": function(){
        	if (!$(".upcomingevents .joel-month").length) {
            	if($(".upcomingevents .sw-calendar-block-title").length){
                	$(".upcomingevents").modEvents({
                        columns     : "yes",
                        monthLong   : "no",
                        dayWeek     : "yes"
                    });
                    eventsByDay(".upcomingevents .ui-articles");
                }

		    }

		    function eventsByDay(container) {
			    var countLi = 0;
			    $("li", container).each(function(){
			        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
			        	var prevDate = $(this).prev().find("h1.ui-article-title.sw-calendar-block-date").html();

			          $('.ui-article', this).addClass('upcoming-column-container ui-clear').prepend('

' + prevDate + '');
			      		$('.ui-article-description', this).wrap('
');
			        };

			        //add an ALL DAY label if no time was given
					$('.upcoming-column.right .ui-article-description', this).each(function(){
						if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
							$(this).prepend('ALL DAY');
						}
						$(".sw-calendar-block-time", this).appendTo($(this));
					});
			    });

			    var eventCount = 0;

			    monthArray = [];

			    $(".ui-article", container).each(function(){
					var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
					var newDate = '';
					var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

					//add a zero to the date if it is a single digit
					if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

					//if we have the day of the week make sure to include it.
					if( dateIndex > 1 ){ 
						newDate += newDateTime[0] + "";
					}

					//add in the month
					newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

					//wrap the date in a new tag
					newDate += '' + newDateTime[dateIndex] + '';

					//append the date and month back into their columns
					$('.upcoming-column.left h1', this).html(newDate);	


					//add an ALL DAY label if no time was given
					$('.upcoming-column.right .ui-article-description', this).each(function(){
						if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
							$(this).prepend('ALL DAY');
						}
					});

					//ADD MONTH CLASS TO CONTAINER
					var month = $(this).find(".joel-month").text().trim();


					//monthArray.indexOf(month) === -1 ? monthArray.push(month) : "";		
					if(monthArray.indexOf(month) === -1){
						monthArray.push(month);
						eventCount = 0;
						$(this).parent().addClass(month+" num-"+eventCount);
					} else {
						eventCount++;
						$(this).parent().addClass(month+" num-"+eventCount);
					}

					//MOVE DAY NAME TO RIGHT COLUMN
					var day = $(this).find(".joel-day").text().trim();
					$(this).find(".sw-calendar-block-time").prepend(""+day+" ");


				});

                $("#foreground-top div.ui-widget.app.upcomingevents .ui-article:first").parent().addClass("active top");
                for (i = 0; i < monthArray.length; i++) { 
                    $(".ui-articles li."+monthArray[i]+":last").addClass("last");
                }
			}


        },

