        "Footer": function() {
        	// FOR SCOPE
            var template = this;

            //SHOW USER OPTIONS IF THEY ARE LOGGED IN
            if($("#sw-mystart-account").length){
                $(".ac-dropdown-container.user-options").show();
                $(".ac-mystart-button.guest").remove();
                $("#sw-mystart-mypasskey span").text("Passkeys");
                $("#sw-mystart-mypasskey").addClass("ac-mystart-button").removeClass("sw-mystart-button").insertAfter("#gb-mystart-right .ac-dropdown-container.school-list");
            } else {
                 //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
                  if(!$("#ui-btn-register").length){
                      $("#sign-in-link").show();
                  } else {
                  	$("#gb-mystart-right").addClass("register-button");
                    $(".ac-mystart-button.guest").show();
                    $(".ac-dropdown-container.user-options").remove();
                  }
            }

            // MOVE Bb FOOTER STUFF
			$(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(0) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(2) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(1) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-copyright"));		

			// FOOTER LINKS
			$(".to-top").click(function(e){
				e.preventDefault();
	            		$("html, body").animate({ scrollTop: 0 }, "slow", function() {
	            			$("li.sw-channel-item:first-child > a").focus();
	            		});
			});

            //FOOTER SITE SHORTCUTS
            if($("#gb-page.sp").length){
            	$("#footer-shortcuts .gb-section").load("/readingsd #footer-shortcuts #sw-content-container7", function(){
                	$("#footer-shortcuts #sw-content-container7").attr("id","sw-content-container7-sp");
                    footerShortcuts();
                });
            } else {
            	footerShortcuts();
            }

        	function footerShortcuts(){
            	//WRAP 1ST WORD IN APP HEADER
                $("#footer-shortcuts .app").each(function(){
                    var appHeaderText = $(this).find(".ui-widget-header h1").text();
                    appHeaderText = appHeaderText.split(" ");
                    var numWords = appHeaderText.length;

                    var newAppHeaderText = ""+appHeaderText[0]+"";

                    for(i = 1;i < numWords;i++){
                        newAppHeaderText += appHeaderText[i]+" ";
                    }

                    newAppHeaderText = newAppHeaderText.trim();

                    $(this).find(".ui-widget-header h1").html(newAppHeaderText).attr({
                        "tabindex":"0",
                        "aria-haspopup":"true",
                        "aria-expanded":"false",
                        "role":"button"
                    });
                    $(this).find(".ui-widget-detail").attr("aria-hidden","true");
                })

                //SHORTCUT ICONS
                $("#footer-shortcuts .ui-widget-detail li").each(function(){
                	if($("img", this).length){
                    	$(this).addClass("img-link");
                    	var linkText = $("img", this).attr("alt");
                        $("> a", this).append(""+linkText+"");
                    }
                })

                //INTERACTION
                $("#footer-shortcuts .ui-widget-header h1").on('click keypress', function(event){
                    if(template.AllyClick(event) === true){
                        //DONT LET THE PAGE JUMP ON KEYPRESS
                        event.preventDefault();
                        if($("#footer-shortcuts").hasClass("open")){
                            $("#footer-shortcuts .ui-widget-detail").slideUp().attr("aria-hidden","true");
                            $("#footer-shortcuts").removeClass("open");
                            $("#footer-shortcuts .ui-widget-header h1").attr("aria-expanded","false");
                        } else {
                            $("#footer-shortcuts .ui-widget-detail").slideDown().attr("aria-hidden","false");
                            $("#footer-shortcuts").addClass("open");
                            $("#footer-shortcuts .ui-widget-header h1").attr("aria-expanded","true");
                        }
                    }
                })
            }

            $(".ac-dropdown-container .selector").on('click keypress', function(event){
                if(template.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    controlDropdowns(event);
                }
            })

            //GLOBALLY CLOSE DROPDOWNS WHEN HOVERED AWAY FROM
            $(".ac-dropdown-container").hover(function(e) {
                e.preventDefault();
            }, function() {

                $(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                $(this).removeClass("open"); 
            })

            function controlDropdowns(event){
                var dropdownToOpen = $(event.currentTarget).parent().find(".ac-dropdown-list").attr("id");
                if($("#"+dropdownToOpen).parent().parent().hasClass("open")){
                    $("#"+dropdownToOpen).parent().attr("aria-hidden", "true").slideUp(300, "swing"); 
                    $("#"+dropdownToOpen).parent().parent().removeClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","false");

                } else {
                    $("#"+dropdownToOpen).parent().attr("aria-hidden","false").slideDown(300, "swing", function(){
                        setTimeout(function(){
                            $("#"+dropdownToOpen).find("li:first a").focus();
                        }, 100);
                    });
                    $("#"+dropdownToOpen).parent().parent().addClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","true");
                }
            }

            //BACK TO TOP
            $("#to-top").click(function(e){
                e.preventDefault();
                        $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                            $("li.sw-channel-item:first-child > a").focus();
                        });
            });
        },

        "Slideshow": function(rotatorOptions) {
            if(rotatorOptions.video == false){
                $("#hp-player").remove();
                if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
                    $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                        "efficientLoad" : "yes",
                        "imageWidth" : 1500,
                        "imageHeight" : 915,
                        "appHeader" : "no",
                        "titles" : "no",
