        "Search": function() {
        	// FOR SCOPE
            var template = this;

            //SEARCH INTERACTION
            $("#search-control").on('click keypress', function(event){
            	var searchControl = this;

		        if(template.AllyClick(event) === true){
					//DONT LET THE PAGE JUMP ON KEYPRESS
		            event.preventDefault();
		            if($(this).parent().hasClass("open")){
		            	$(searchControl).parent().removeClass("open");
                        $(searchControl).attr("aria-expanded","false");
		            	$("#foreground-top .hp.column.two,#sp-header-right").removeClass("open-search");
		            	$("#gb-search").animate({
							width: "47"
						}, 500, function() {
							$("#gb-search-form").hide().attr("aria-hidden","true");

						});
		            } else {
		            	$(searchControl).parent().addClass("open");
                        $(searchControl).attr("aria-expanded","true");
		            	$("#foreground-top .hp.column.two, #sp-header-right").addClass("open-search");
		            	$("#gb-search").animate({
							width: "346"
						}, 500, function() {
							$("#gb-search-form").show().attr("aria-hidden","false");
		            		$("#gb-search-input").focus();
		            	});

		        	}
	    		}
	    	})
        },

        "AllyClick": function(event) {
            if(event.type === 'click'){
                return true;
            } else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if((code === 32)|| (code === 13)){
                    return true;
                }
            } else{
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "AccessibilityFeatures": function(){
        	$("#sw-mystart-mypasskey").attr({
                "aria-label":	"Open My Passkey",
                "role":		"button"
            });

            // KEY MAPPING
            var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

            //GLOBAL KEYBOARD NAV FOR DD SELECTORS
            $(".ac-dropdown-container .selector").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                        } else {
                            e.preventDefault();

                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");


                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true
                            }

                            switch(parentContainer){
                                case "user-options":
                                    $("#gb-mystart-right .to-top").focus();
                                break;
                                case "translate":
                                    $(".ac-dropdown-container.school-list .selector").focus();
                                break;
                                case "school-list":
                                	if(loggedIn == true){
                                    	$("#ui-btn-mypasskey").focus();
                                    } else {
                                    	$(".ac-mystart-button.signin a").focus();
                                    }

                                break;
                            }

                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.down:
                    case csKeyCodes.right:
                        e.preventDefault();

                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();

                    break;
                }
            });

            //GLOBAL KEYBOARD NAV FOR DD LINKS
            $(".ac-dropdown-container li a").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();

                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                      $(".ac-mystart-button.spanish a").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "user-options":
                                      $("#ui-btn-mypasskey").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "school-list":
                                      $(".ac-dropdown-container.translate .selector").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;
                            }
                        } else {
                            e.preventDefault();

                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true
                            }
                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                      $(".ac-dropdown-container.school-list .selector").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "user-options":
                                      $("#gb-mystart-right .to-top").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "school-list":
                                	if(loggedIn == true){
                                    	$("#ui-btn-mypasskey").focus();
                                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      	$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      	$(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                    } else {
                                    	$(".ac-mystart-button.signin a").focus();
                                      	$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      	$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      	$(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                    }

                                break;
                            }


                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:last-child > a").focus();
                    break;

                    case csKeyCodes.esc:
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                    break;
                }
            });
        }
    };

//CREATE OBJECT FOR ROTATOR OPTIONS
var rotatorOptions = {};

rotatorOptions.video = false;

if(rotatorOptions.video == true){

    //loads the IFrame Player API code asynchronously.
    var tag = document.createElement('script');

    tag.src = "//www.youtube.com/iframe_api";
    var firstScriptTag = document.getElementsByTagName('script')[0];
    firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);    

    //CHECK TO SEE IF WE ARE ON A MOBILE DEVICE
    if($("html.mobile").length){
        $(function(){
            $("#gb-page").addClass("full-screen video-bg image-bg");
            csVideoResize();
        });
    } else {
        $(function(){
            $("#gb-page").addClass("full-screen video-bg");
            $("#video-bg-wrap iframe").attr("tabindex","-1");
        });

        //creates an  (and YouTube player)
        //after the API code downloads.
        var player;
        var windowWidth;
        var windowHeight;
        var videoToggle = 'true';
        function onYouTubeIframeAPIReady() {
                windowWidth = $(window).width();
                windowHeight = $(window).height();
                player = new YT.Player('player', {
                    height: windowHeight,
                    width: windowWidth,
                    videoId: '9ZfN87gSjvI',
                    playerVars: { 
                        'modestbranding': 1,
                            'controls': 0, 
                            'rel' : 0,
                            'loop': 1,
                            'wmode': 'opaque'
                    },
                    events: {
                        'onReady': onPlayerReady,
                        'onStateChange': onPlayerStateChange
                    }
                });
        }
    }
}

//the API will call this function when the video player is ready.
function onPlayerReady(event) {
    if(!$("html").hasClass("mobile")) {
        player.mute();
        event.target.playVideo();
    } else {
        $("#play-pause-btn").attr("aria-label","Play Video");
        $("#play-pause-btn").removeClass("pause");
    }

    csVideoResize();

    $("#play-pause-btn").click(function() {
        $(this).toggleClass("pause");
        $(this).toggleClass("play");
        pausePlayVideo();
    });
}

//The API calls this function when the players state changes.
//The function indicates that when playing a video (state=1),
//the player should play for six seconds and then stop.
var done = false;
function onPlayerStateChange(event) {
    if(!$("html").hasClass("mobile")) {
        pausePlayVideo();
    }
}
function pausePlayVideo() {
    if($("#play-pause-btn").hasClass("pause")) {
        $("#play-pause-btn").attr("aria-label","Pause Video");
        player.playVideo();
        //player.mute();
    } else {
        $("#play-pause-btn").attr("aria-label","Play Video");
        player.pauseVideo();
    }
}

function stopVideo() {
    player.stopVideo();
}

function csVideoResize() {
    // FOR SCOPE
        var template = this;

    if($("#player").length) {
        switch(CreativeTemplate.GetBreakPoint()) {
            case "desktop":
                var topOffSet = $("#hp-player").offset().top;
                var screenWidth = $(window).width();
                var screenHeight = $(window).height() - topOffSet;
                var screenRatio = screenHeight / screenWidth;
                            var containerHeight = screenWidth * screenRatio;
                var vidWidth = screenWidth, vidHeight = 0;

                // GET IMAGE RATIO
                var initW = 853.5, initH = 480;
                var ratio = initH / initW;
                vidHeight = vidWidth * ratio;

                if(vidHeight < screenHeight){
                    vidHeight = screenHeight;
                    vidWidth = vidHeight / ratio;
                }

                            var centerWidth = (vidWidth - screenWidth) / 2;
                            var centerHeight = (vidHeight - screenHeight) / 2;

                // APPLY THE DIMENSIONS
                $("#hp-player").css({"height": containerHeight + "px"});
                $("#player").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
            break;
            case "768": case "640": case "480": case "320":
                var screenWidth = $(window).width();
                var screenHeight = $(window).height();
                var vidWidth = screenWidth, vidHeight = 0;

                // GET IMAGE RATIO
                var initW = 853.5, initH = 480;
                var ratio = initH / initW;
                vidHeight = vidWidth * ratio;

                // APPLY THE DIMENSIONS
                $("#hp-player").css({"height": "auto"});
                $("#player").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
            break;
        }
    }
}

