$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=7klzDXXIbqpRx0pHfhX5tQ%3d%3d&SessionID=583c5c2c-8d2e-42ab-8ec3-98bc2c05310a&SiteID=4&ChannelID=6&SectionID=3903&PageID=2330&HitDate=4%2f22%2f2020+12%3a02%3a54+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.75.167';
