(function($) {
	$(document).ready(function() {
 $( '.primary_2588 ul.sub-menu li a, .primary_2588 ul.submenu-items li a' ).prepend( '' );

			$( '.primary_2588 .mega-item.wr-megamenu-fixed, .primary_2588 .menu-item-has-children.menu-default' ).on( 'hover', function () {

				var
					mega_inner = null,
					mega_menu = $(this).find( ' > .wr-megamenu-inner' ),
					sub_menu = $(this).find( ' > .sub-menu' ),
					is_mega_inner = false
					;

				if (sub_menu.length > 0) {
					mega_inner = sub_menu;
				} else if (mega_menu.length > 0) {
					mega_inner = mega_menu;
					is_mega_inner = true;
				}
				if (mega_inner != null) {
					if (mega_inner.outerWidth() > ($(window).outerWidth() - mega_inner.offset().left ) ) {
						if (!is_mega_inner) {
							mega_inner.addClass( 'wr-megamenu-rtl' );
							$( '.primary_2588 ul.sub-menu li.wr-megamenu-item' ).css({'text-align': 'right', 'background': 'inherit'});
							$( '.primary_2588 ul.wr-mega-menu ul ul.sub-menu' ).css( 'right', '99%' );
							$( '.primary_2588 ul.wr-mega-menu ul li' ).on( 'hover', function () {
								$(this).find( '> ul.sub-menu' ).css({'right': '100%'});
							});
							$( '.wr-megamenu-container.primary_2588 ul ul' ).addClass('pull-right');
						} else {
							mega_inner.addClass('wr-megamenu-left-inner');
						}
					}
				}
			});

	});
})(jQuery);
