      		Date.prototype.getMonthName = function() {
		var m = ['January','February','March','April','May','June','July',
		'August','September','October','November','December'];
		return m[this.getMonth()];
		}
		Date.prototype.getDayName = function() {
		var d = ['Sunday','Monday','Tuesday','Wednesday',
		'Thursday','Friday','Saturday'];
		return d[this.getDay()];
		}
		var today = new Date;
		$("div#current-date").html(today.getDayName() + ',  ' + today.getMonthName() + ' ' + today.getDate()); 
      		$("div#cycle-date").html("Cycle Day " + cycleDay());
        }
        function addTranslate() {
		$("div#sw-mystart-left").append("
Translate Back To English");
		$("div.sw-mystart-button.translateBack").click(function () {
			$.cookies.del('swTranslationCookie');
		});
		if($.cookies.get('swTranslationCookie')) {
			var langCode = $.cookies.get('swTranslationCookie');
			$("body").translate("english", langCode,{
				toggle: true,
				fromOriginal:true,
				start: function(translation) {
					joelGrowl("Translating...", "translating",0);
				},
				complete: function(translation) {
					$(".joelGrowl.translating").text("translation complete!");
					$(".joelGrowl.translating").delay(1000).fadeOut('fast', function(){
						$(this).remove();
					});
				}
			});
			$("div.sw-mystart-button.translateBack").show();
		} else {
			$.translate(function(){
			    	$.translate.ui({
			    		filter: ["es", "ar", "zh-CN", "zh-TW", "fr", "de", "el", "it", "ja", "ko", "pt", "ru"]
			    	})
			    	.appendTo('#sw-mystart-left');
			    	$(".sw-mystart-dropdown.translate").click(function() {
					$(".sw-dropdown",this).show();
				});
				$(".sw-mystart-dropdown.translate").hover(function() {
				},
				function(){
					$(".sw-dropdown",this).hide();
				});
				$(".jq-translate-ui li a").click(function() {
					var langCode = $(this).attr("id");
					$("body").translate("english", langCode,{
						toggle: true,
						fromOriginal:true,
						start: function(translation) {
							joelGrowl("Translating...", "translating",0);
						},
						complete: function(translation) {
							$(".joelGrowl.translating").text("translation complete!");
							$(".joelGrowl.translating").delay(1000).fadeOut('fast', function(){
								$(this).remove();
							});
						}
					})
					$(this).parent().parent().parent().parent().hide();
					$("div.sw-mystart-button.translateBack").show();
					$.cookies.set('swTranslationCookie', langCode);
				});
			});
		}
		/*if(useTranslate == "off" || useTranslate != "on")
		{
			$(".sw-mystart-dropdown.translate").hide();
		}*/
	}
	function equalHeight(){
		var column1 = $("div.hp.column.one").height() - $("div#sw-content-container1").height();
		$("div.hp.column.one div.ui-widget.app div.ui-widget-detail").last().height($("div.hp.column.one div.ui-widget.app div.ui-widget-detail").last().height() + column1);

		var column2 = $("div.hp.column.two").height() - $("div#sw-content-container2").height();
		$("div.hp.column.two div.ui-widget.app div.ui-widget-detail").last().height($("div.hp.column.two div.ui-widget.app div.ui-widget-detail").last().height() + column2);

		var column3 = $("div.hp.column.three").height() - $("div#sw-content-container3").height();
		var sponsorsApp = $("div.ui-widget.app.sponsors").height();
		$("div.hp.column.three div.ui-widget.app div.ui-widget-detail").first().height($("div.hp.column.three div.ui-widget.app div.ui-widget-detail").first().height() + column3 - sponsorsApp);

	}
	function sameHeight(selector)
	{
	  if($("#hp-content").size()){
		selector.height(function(){
			$(this).height($(this).parent().height());

		});
		equalHeight();

		selector.children().resize(function(){
			selector.height("auto");
			selector.height(function(){
				$(this).height($(this).parent().height());
			});
			equalHeight();
		});
	   }	
	   if($("#sp-content").size()){
		selector.height(function(){
			$(this).height($(this).parent().parent().height());
			$("div.sp.column.two").height("auto");
			$("div.sp.column.two").height($("div#sp-content").height() - 68);
		});

		selector.children().resize(function(){
			selector.height("auto");
			selector.height(function(){
				$(this).height($(this).parent().parent().height());
				$("div.sp.column.two").height("auto");
			        $("div.sp.column.two").height($("div#sp-content").height() - 68);
			});
		});
	    }	
	}

