link.script:
$(document).ready(function() {
		var folderID = "12031";
	$.ajax({
	    url: '/controls/SiteNav.ashx?folder='+folderID+'&id=newmain-nav&levelclass=menulevel',
	    cache: false,
	    success: function(menuHTML){
			// append all this into the #content-section div
			$('#content-section').append(menuHTML);
			// activate accordion
			$('#newmain-nav').accordion({
				active: false,
				event: 'click',
				navigation: true,
				collapsible: true,
				clearStyle: true,
				heightStyle: "content",
				create : function (event, ui) {
					// Find the currently active accordion index
					var active = $('#newmain-nav').accordion('option','active');
					// Loop through all the links in the nav
					$('#newmain-nav a').each( function() { 
						// If the current page url contains the href text (we have a match!)
						if (window.location.href.indexOf($(this).attr('href')) > -1) {
							// Disable the link and apply a css class 'nav-selected'
							$(this).addClass("nav-selected").attr('href','javascript:void(0);');
							// Check to see if this accordion is open
							var header = $(this).closest('.ui-accordion-header').parent().index();
							header = (header == -1)? $(this).closest('.ui-accordion-content').prev().parent().index() : header;
							if (header !== active && header !== -1) {
								// Open the accordion and erase the cookie
								$('#newmain-nav').accordion('option','active',header);
								eraseCookie('menustate-' + folderID );
							}
						}
					});

					// Read the cookie to see if we're opening an accordion from last page
					var hid = readCookie('menustate-' + folderID );
					if (hid != null && hid.length) {
						// Open the accordion
						$('#newmain-nav').accordion('option','active',$('#'+hid).closest('li').index());						
					}
					eraseCookie('menustate-' + folderID );


				},
				beforeActivate : function(event, ui) {
					var hid = ui.newHeader.attr('id');

					if (hid === undefined) {
						eraseCookie('menustate-' + folderID );
					} else {
						createCookie('menustate-' + folderID, hid, 2 );
					}
				}
			});

			// prevent plus/minus icon from displaying next to regular links
			// by adding the "noexpand" class to the  in front of them
			$(".menulevel1 > a").siblings().addClass("noexpand");

			// Catch the link clicks in the menu and set the cookie
			$("#newmain-nav > li > ul a").click(function() {
				var hid = $('#newmain-nav .ui-state-active').attr('id');
				if (hid !== undefined) {
					createCookie('menustate-' + folderID, hid, 2 );
				}
				return true;
			});

			// restore click function to regular links
			$('.menulevel1 > a').click(function () {
				if ($(this).attr("target") === "_blank"){
					window.open ($(this).attr("href"));
				} else {
					window.location = ($(this).attr("href"));
				}
				return true;
			});
	    }
	});


// The following code is for getting/setting/erasing cookies and you can place it anywhere
// It does NOT have to be in the $(document).ready function

function createCookie(name,value,days) {
	if (days) {
		var date = new Date();
		date.setTime(date.getTime()+(days*24*60*60*1000));
		var expires = "; expires="+date.toGMTString();
	}
	else var expires = "";
	document.cookie = name+"="+value+expires+"; path=/";
}

function readCookie(name) {
	var nameEQ = name + "=";
	var ca = document.cookie.split(';');
	for(var i=0;i < ca.length;i++) {
		var c = ca[i];
		while (c.charAt(0)==' ') c = c.substring(1,c.length);
		if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
	}
	return null;
}

function eraseCookie(name) {
	createCookie(name,"",-1);
}
    });

