function rssSumVis(id) {
   if (document.getElementById('ctl18_sum' + id).style.display == 'none'){
       document.getElementById('ctl18_exp' + id).src = '/QCore/images/linksMinus.gif';
       document.getElementById('ctl18_sum' + id).style.display = 'block';
   } else {
       document.getElementById('ctl18_exp' + id).src = '/QCore/images/linksPlus.gif';
       document.getElementById('ctl18_sum' + id).style.display = 'none';
   }
}
