  var fixedTopSelector = ".fixedTopTS";
  var anchorSelector = "#divToolbars";

  function clamp(number, min, max, offset = 0) {
    return Math.min(Math.max(number + offset, min + offset), max + offset);
  }

  function fixedNavAdjustment() {
    var fixedHeader = $(fixedTopSelector);
    var anchor = $(anchorSelector);
    if (fixedHeader.length === 0) return;

    if ($('#doneEditing').length > 0 || typeof DesignCenter !== "undefined") {
      fixedHeader.css('position', 'absolute');
    } else {
      var initialTop = parseInt(fixedHeader.css("top"), 10);
      var topOfViewport = $(window).scrollTop();
      var anchorPos = 0;
      if (anchor.length) {
        var anchorBox = anchor[0].getBoundingClientRect();
        anchorPos = anchorBox.bottom;
      }
      // topOfViewport += anchorPos;

      var offset = clamp(anchorPos, anchorPos, anchorPos);
      if (!window.matchMedia('screen and (min-width: 63em)').matches) {
        offset = 0;
      }

      fixedHeader.css("margin-top", offset);
    }
  };

  $(window).load(function () {
    setTimeout(function () {
      fixedNavAdjustment();
    }, 10);

    $(window).scroll(function () {
      fixedNavAdjustment();
    });

    var navAdjustTimeout_ts;
    $(window).resize(function () {
      clearTimeout(navAdjustTimeout_ts);
      navAdjustTimeout_ts = setTimeout(function () {
        fixedNavAdjustment();
      }, 150);
    });
    $.when(window.Pages.angularToolbarComplete).done(function () {
      fixedNavAdjustment();
    });
  });
