    $(function() {
    	CreativeTemplate.Init();
        csGlobalJs.OpenInNewWindowWarning();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            //HOMEPAGE HEADLINES SETUP
            hfSliderParams = {};

            hfSliderParams.screenWidth = $(window).width();
            hfSliderParams.count = 0;
            hfSliderParams.slideContainer = $("#hp-content-top .headlines ul");

            this.SetTemplateProps();
            this.modMyStart();
            this.modEvents();
            this.Header();
            this.Body();
            this.Footer();
            this.Homepage();
            this.HomepageEvents();
            this.Slideshow();
            this.JsMediaQueries();
            this.HomepageHeadlinesSlider();
            this.AppAccordion();
            this.Search();


            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });

        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
        	this.SchoolList();
            this.ChannelBar();

            this.CustomAlert();

            //SET DEFAULT HEADLINES IMAGES
            $("#hp-content-bottom .hp.column.one .headlines ul.ui-articles li").each(function(){
            	if(!$(".ui-article-thumb", this).length){
                    var defaultImage = '/cms/lib/SC02209139/Centricity/Template/GlobalAssets/images///images/hcs-default-headlines.jpg';

                    $(".ui-article", this).prepend('');
                    $(this).addClass("default-image");
                }
            }); 


        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.HomepageHeadlinesSlider();
            this.centerSlideshow();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    hfSliderParams.numFullSlides = 3;
                    if(hfSliderParams.screenWidth >= 1200){
                		hfSliderParams.slideWidth = 372;
                	} else {
                		hfSliderParams.slideWidth = 372;
                	}       
                break;
                case "768":
                    hfSliderParams.numFullSlides = 2;
                    hfSliderParams.slideWidth = 346; 
                break;
                case "640":
                    hfSliderParams.numFullSlides = 1;
                    hfSliderParams.slideWidth = 346;
                break;
                case "480":
                    hfSliderParams.numFullSlides = 1;
                    hfSliderParams.slideWidth = 346;
                break;
                case "320":
                    hfSliderParams.numFullSlides = 1;
                    hfSliderParams.slideWidth = 260;
                break;
            } 
        },

        "CustomAlert": function() {
        	if(useCustomAlert == "yes"){
             	$("body").attr("data-alert","custom");

                //CUSTOM TITLE
                $("#onscreenalert-message-dialog-icon .titleMessage").text(alertBoxTitle);

                //BORDER COLOR
                $(".onscreenalert-ctrl-content").css("border","4px solid "+alertBoxBorderColor);

                //ALERT IMAGE
                $("#onscreenalert-ctrl-content-icon").css({
                	"background-image":"url("+alertBoxCircleImage+")",
                    "border-color":alertBoxCircleBorderColor,
                    "visibility":"visible"
                });

            	$("#onscreenalert-message-dialog-icon").css("visibility","visible");
                $("#sw-important-message-tooltip").text("HCS Information");
            } else {
            	$("body").attr("data-alert","default");
            }

        },

        "modMyStart": function() {

        	// ADD TRANSLATE
        	$("#header-mystart").creativeTranslate({
                "type": 1,
                "advancedOptions": {
                    "addMethod": "append",
                    "dropdownHandleText": "TRANSLATE",
                }
            });
            $(".sw-dropdown-selected").remove();
            $(".sw-mystart-dropdown.translate .selector").addClass("district-font").attr({
                "tabindex":"0",
                "aria-expanded":"false",
                "aria-haspopup":"true",
                "aria-label":"Translation Options",
                "role":"button",
                "aria-controls":"ac-translate-list"
            });
            $(".sw-mystart-dropdown.translate .sw-dropdown-list").addClass("ac-dropdown-list").removeClass("sw-dropdown-list");
            $(".sw-mystart-dropdown.translate .sw-dropdown").addClass("ac-dropdown").removeClass("sw-dropdown").attr("aria-hidden","true");
            $(".sw-mystart-dropdown.translate").attr("class","").addClass("ac-dropdown-container translate").removeAttr("tabindex");
            $(".ac-dropdown-container.translate .ac-dropdown-list").attr("id","ac-translate-list");

            var pageURL = window.location.href;
            pageURL = encodeURIComponent(pageURL);
            $('
En Español').insertBefore(".ac-dropdown-container.translate");

            //BUILDING MY OWN SCHOOLLIST
	        var acSchoolList = '';

	        $(".sw-mystart-dropdown.schoollist li").each(function(){
	        	var schoolItem = $(this).html();
	            acSchoolList += "
"+schoolItem+"";
	        });  

	        //MOVING ANY MYSTART ELEMENTS TO MATCH DESIGN
	        $(".ac-dropdown-container.school-list .ac-dropdown-list").append(acSchoolList);
            $("#sticky-footer .gb-section").css("visibility","visible");
        },

        "Header": function() {
            // FOR SCOPE
            var template = this;

            var showSiteName = false;

            if(showSiteName != false){
                // ADD SITENAME
                var siteNameOne = $.trim("");
                var siteNameTwo = $.trim("");
                if((siteNameOne == "") && (siteNameTwo == "")) {
                    var splitLen = 2;
                    var siteName = "Horry County Schools";
                    siteName = siteName.split(" ");
                    var siteNameLength = siteName.length;
                    if(siteNameLength > 2){
                        siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                    } else {
                        siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                    }
                    siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                    $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
                } else if((siteNameOne != "") && (siteNameTwo == "")) {
                    $("#gb-sitename").prepend("
" + siteNameOne + "");
                } else if((siteNameOne == "") && (siteNameTwo != "")) {
                    $("#gb-sitename").prepend("
" + siteNameTwo + "");
                } else if((siteNameOne != "") && (siteNameTwo != "")) {
                    $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
                    $("#footer-sitename").html(siteNameOne + "
" + siteNameTwo + "");
                }
            }

            //GLOBAL ICONS
            $("#gb-icons").creativeIcons({
                "iconNum"       : "6",
                "defaultIconSrc": "",
                "icons"         : [
                    {
                        "image": "/cms/lib/SC02209139/Centricity/Template/GlobalAssets/images///icons/ico-registration.png",
                        "showText": true,
                        "text": "Registration",
                        "url": "/Page/10126",
                        "target": "_self"
                    },
                    {
                        "image": "/cms/lib/SC02209139/Centricity/Template/GlobalAssets/images///icons/HCS-dollaricon.png",
                        "showText": true,
                        "text": "MyPaymentsPlus",
                        "url": "https://www2.mypaymentsplus.com/welcome",
                        "target": "_self"
