        "SchoolList": function(){
        	// FOR SCOPE
            var template = this;

            //CREATE AN EMPTY ARRAY FOR SCHOOL CATEGORIES
            schoolCategories = [];
            fullSchoolCategories = [];

            //LOOP THROUGH EACH SCHOOL TO COLLECT CATEGORY NAMES
            for(var i in schoolList) {
                var schoolType = schoolList[i].schoolType.split(" ");
                schoolType = schoolType[1];

                var fullSchoolType = schoolList[i].schoolType;



                //IF THE CATEGORY DOES NOT ALREADY EXIST IN THE ARRAY, PUSH IT INTO THE ARRAY
                schoolCategories.indexOf(schoolType) === -1 ? schoolCategories.push(schoolType) : "";  
                fullSchoolCategories.indexOf(fullSchoolType) === -1 ? fullSchoolCategories.push(fullSchoolType) : ""; 
            }

            //BUILD THE SCHOOL CATEGORIES TABS
            schoolCategoriesHTML = '';
            schoolTabPanelsHTML = '';

            //LOOP THROUGH EACH CATEGORY AND CREATE THE HTML FOR THE TAB AND THE TAB PANEL
            var mobileSchoolHeadings = [];

            for(var i in schoolCategories) {
                  var schoolCategory = schoolCategories[i].toLowerCase();
                  schoolCategory = schoolCategory.replace("/","-");



                if(i == 0){

                    //THIS IS THE FIRST CATEGORY, WE NEED IT TO BE ACTIVE
                    schoolCategoriesHTML += '
'+
                          fullSchoolCategories[i].substring(1).trim()+
                    '';

                    schoolTabPanelsHTML += '
'+
                          ''+
                    '';

                    mobileSchoolHeadings.push(schoolCategories[i]+" Schools");

                } else if(i == schoolCategories.length -1) {
                    //THIS IS THE LAST CATEGORY

                    schoolCategoriesHTML += '
'+
                          fullSchoolCategories[i].substring(1).trim()+
                    '';

                    schoolTabPanelsHTML += '
'+
                          ''+
                    '';

                    mobileSchoolHeadings.push(schoolCategories[i]+" Schools");
                } else {

                    schoolCategoriesHTML += '
'+
                          fullSchoolCategories[i].substring(1).trim()+
                    '';

                    schoolTabPanelsHTML += '
'+
                          ''+
                    '';

                    mobileSchoolHeadings.push(schoolCategories[i]+" Schools");
                }
            }

            //INSERT THE SCHOOL CATEGORY TABS AND THE SCHOOL TAB PANEL STRUCTURE INTO THE PAGE
            $("#school-types").prepend(schoolCategoriesHTML);
            $("#school-listings").append(schoolTabPanelsHTML);

            //LOOP THROUGH EACH SCHOOL, AND PLACE IT IN THE PROPER LIST
            for(var i in schoolList) {
                var schoolCat = schoolList[i].schoolType.toLowerCase().split(" ");
                schoolCat = schoolCat[1];
                schoolCat = schoolCat.replace("/","-");

                if(schoolList[i].schoolVisible == 'true' || schoolList[i].schoolVisible == 'TRUE') {

                      $("#"+schoolCat+"-schools ul").append('
' + schoolList[i].schoolName + '');

                }
            }
            template.RsMenu(mobileSchoolHeadings);
            schoolListInteract();
            template.AccessibilityFeatures();

            function schoolListInteract(){
            	//OPEN AND CLOSE SCHOOL MENU
                $(".ac-mystart-button.schools").on('click keypress', function(event){
                    if(template.AllyClick(event) === true){
                        //DONT LET THE PAGE JUMP ON KEYPRESS
                        event.preventDefault();

                        if($(this).hasClass("open")){
                            $("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                            $(this).attr("aria-expanded","false");
                            $(this).removeClass("open");
                        } else {
                            $("#school-list-container").slideDown(300, "swing").attr("aria-hidden","false");
                            $(this).attr("aria-expanded","true");
                            setTimeout(function(){
                                $(".school-tab:first-child").focus();
                            }, 100);
                            $(this).addClass("open");
                        }
                    }
                })

                //SCHOOL TAB NAV
                $(".school-tab").on('focus', function(){
                    var tabNum = $(this).index();

                    $(".school-tab").removeClass("active").attr("tabindex","-1");
                    $(this).addClass("active").attr("tabindex","0");

                    $(".school-listing-container").hide().removeClass("active");
                    $(".school-listing-container").eq(tabNum).show().addClass("active");
                })
            }


        },

