            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home").show();

            // ADJUST MORE LINKS
            $("a.more-link span").text("View All");

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
Departments & Services");
            }

        },

        "Footer": function() {
        	// FOR SCOPE
            var template = this;

            //SHOW USER OPTIONS IF THEY ARE LOGGED IN
            if($("#sw-mystart-account").length){
                $("#ac-user-options .guest").remove();
                $("#ac-user-options .user").show();
                $("#sw-mystart-mypasskey span").text("Passkeys");
                $("#sw-mystart-mypasskey").addClass("ac-mystart-button").removeClass("sw-mystart-button").insertAfter(".ac-mystart-button.extra-link");
            } else {
                 //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
                  if(!$("#ui-btn-register").length){
                      $("#ac-user-options #sign-in-link").show();
                  } else {
                    $("#ac-user-options .guest").show();
                  }
            }

            // MOVE Bb FOOTER STUFF
			$(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(0) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(2) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(1) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-copyright"));		

            //2ND LINE IN ADDRESS
            if($("#address span:first-child br").length){
                var entireAddress = $("#address span:first-child").html();
                var addressArray = entireAddress.split(">");
                var addressOne = addressArray[0];
                var addressTwo = addressArray[1];
                $("#address span:first-child").html(addressOne);
                $(" "+addressTwo+"").insertAfter("#address span:first-child");
            }

			// FOOTER LINKS
			$(".to-top").click(function(e){
            	if(template.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();

                    $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                        $("li.sw-channel-item:first-child > a").focus();
                    });
                }

			});

            $(".ac-dropdown-container .selector").on('click keypress', function(event){
                if(template.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    controlDropdowns(event);
                }
            })

            //GLOBALLY CLOSE DROPDOWNS WHEN HOVERED AWAY FROM
            $(".ac-dropdown-container").hover(function(e) {
                e.preventDefault();
            }, function() {

                $(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                $(this).removeClass("open"); 
            })

            function controlDropdowns(event){
                var dropdownToOpen = $(event.currentTarget).parent().find(".ac-dropdown-list").attr("id");
                if($("#"+dropdownToOpen).parent().parent().hasClass("open")){
                    $("#"+dropdownToOpen).parent().attr("aria-hidden", "true").slideUp(300, "swing"); 
                    $("#"+dropdownToOpen).parent().parent().removeClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","false");

                } else {
                    $("#"+dropdownToOpen).parent().attr("aria-hidden","false").slideDown(300, "swing", function(){
                        setTimeout(function(){
                            $("#"+dropdownToOpen).find("li:first a").focus();
                        }, 100);
                    });
                    $("#"+dropdownToOpen).parent().parent().addClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","true");
                }
            }

            //BACK TO TOP
            $("#to-top").click(function(e){
                e.preventDefault();
                        $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                            $("li.sw-channel-item:first-child > a").focus();
                        });
            });
        },

