        "HomepageHeadlinesSlider": function(){
            // FOR SCOPE
            var template = this;

            if(!$("#hp-content-top .headlines").hasClass("initialized")){
                headlinesSlider();
            }
            //CENTER SLIDER ON SCREEN
            if(hfSliderParams.numHeadlines > 3){
                centerHeadlines();
                //SET ATTRIBUTES
                headlinesAttributes();
            }           

            function headlinesSlider(){
                $("#hp-content-top .headlines").addClass("initialized");



                //MOVE MORE LINK
                if($("#hp-content-top .headlines .more-link").length){
                    var moreLinkURL = $("#hp-content-top .headlines .more-link").attr("href");
                    //$("#hp-content-top .headlines li:last-child").remove();
                    //hfSliderParams.numHeadlines = $("#hp-content-top .headlines li").length;
                    $("#hp-content-top .headlines .ui-widget-detail").append("
View All");
                }
                    hfSliderParams.numHeadlines = $("#hp-content-top .headlines li").length;

                hfSliderParams.endingPos = ((hfSliderParams.numHeadlines - hfSliderParams.numFullSlides) * hfSliderParams.slideWidth) * -1;

                //ONLY MAKE THE SLIDER IF THERE ARE MORE THAN 3 HEADLINES
                if(hfSliderParams.numHeadlines > 3){

                    //WRAP HEADLINES UL IN A CONTAINER
                    $("#hp-content-top .headlines ul").wrap("

");

                    $("#hp-content-top .headlines").addClass("slider");

                    hfSliderParams.headlineUlWidth = $("#hp-content-top .headlines li").length * hfSliderParams.slideWidth;
                    $(hfSliderParams.slideContainer).width(hfSliderParams.headlineUlWidth);

                    //ADD CONTROLS
                    $("#hp-content-top .headlines .ui-widget-detail").prepend("");
                    $("").insertAfter("#headline-slider-container");
                } else {
                    $("#hp-content-top .headlines").addClass("no-slider");

                }

                //ADD RSS BUTTON TO CONTROLS CONTAINER
                $("#hp-content-top .headlines .app-level-social-rss").prependTo("#headline-controls");

                //NAVIGATION
                $(".headlines .slider-button").on('click keypress', function(event){

                    if(template.AllyClickForButtons(event) === true){

                        if(!$(hfSliderParams.slideContainer).hasClass("animating")){

                            headlinesSliderButtons(event);
                        }

                    }

                })
            }

            function headlinesAttributes(){
                //REMOVE ALL HEADLINES ITEMS FROM THE TAB INDEX
                $("#hp-content-top .headlines ul li a").attr("tabindex","-1");
                $("#hp-content-top .headlines ul li").attr("aria-hidden","true");

                //ADD ONLY FULLY VIEWABLE HEADLINES TO THE TAB INDEX
                var lastTabbable = hfSliderParams.count + hfSliderParams.numFullSlides;
                for (i = hfSliderParams.count; i < lastTabbable; i++) { 
                    $("#hp-content-top .headlines ul li:eq("+i+") .ui-article-title a").attr("tabindex","0");
                    $("#hp-content-top .headlines ul li:eq("+i+")").attr("aria-hidden","false");
                }
            }

            function centerHeadlines(){

                hfSliderParams.headlineUlWidth = $("#hp-content-top .headlines li").length * hfSliderParams.slideWidth;
                $(hfSliderParams.slideContainer).width(hfSliderParams.headlineUlWidth);
                $(hfSliderParams.slideContainer).css("left","0px");
                hfSliderParams.count = 0;

                hfSliderParams.endingPos = ((hfSliderParams.numHeadlines - hfSliderParams.numFullSlides) * hfSliderParams.slideWidth) * -1;
            }

            function headlinesSliderButtons(event){
                var controlButton = $(event.target);      

                if($(controlButton).hasClass("prev")){
                    //ONLY SLIDE IF THE FIRST SLIDE IS NOT IN THE FIRST POSITION
                    if(hfSliderParams.count != 0){
                        $(hfSliderParams.slideContainer).addClass("animating");
                        $(hfSliderParams.slideContainer).animate({ left: '+='+hfSliderParams.slideWidth }, "slow", function() {
                            hfSliderParams.count--;
                            headlinesAttributes();
                            $(hfSliderParams.slideContainer).removeClass("animating");
                        });
                    } else {
                        $(hfSliderParams.slideContainer).animate({ left: hfSliderParams.endingPos }, "slow", function() {
                            hfSliderParams.count = hfSliderParams.numHeadlines - hfSliderParams.numFullSlides;
                            headlinesAttributes();
                            $(hfSliderParams.slideContainer).removeClass("animating");
                        });
                    }
                } 

                  //IF THE NEXT BUTTON IS CLICKED, SLIDE TO THE LEFT
                  if($(controlButton).hasClass("next")){
                    if(hfSliderParams.count != hfSliderParams.numHeadlines - hfSliderParams.numFullSlides){
                        $(hfSliderParams.slideContainer).addClass("animating");
                        $(hfSliderParams.slideContainer).animate({ left: '-='+hfSliderParams.slideWidth }, "slow", function() {
                            hfSliderParams.count++;
                            headlinesAttributes();
                            $(hfSliderParams.slideContainer).removeClass("animating");
                        }); 
                    } else {
                        $(hfSliderParams.slideContainer).animate({ left: 0 }, "slow", function() {
                            hfSliderParams.count = 0;
                            headlinesAttributes();
                            $(hfSliderParams.slideContainer).removeClass("animating");
                        });

                    }   

                  }
            }
        },

        "HomepageEvents": function(){

            // FOR SCOPE
            var template = this;

            var totalEventDays = $("#hp-content-middle .upcomingevents li").length;
            var viewCalendarURL = $("#hp-content-middle .upcomingevents .view-calendar-link").attr("href");

            if(totalEventDays > 5 && $("#hp-content-middle .upcomingevents").length){
                //ADD VIEW CALENDAR LINK AND EXPAND EVENTS BUTTON
                $("#hp-content-middle").append("

Expand EventsView Calendar");

                eventA11y();

                $("#expand-collapse").on('click keypress', function(event){
                    if(template.AllyClickForButtons(event) === true){
                        //DONT LET THE PAGE JUMP ON KEYPRESS
                        event.preventDefault();

                        var events = $("#hp-content-middle");
                        var clickedButton = $(this);
                        var initialHeight = 265;

                        if(events.hasClass("open")){
                            events.animate({
                                height: initialHeight
                            }, 1000, function() {
                                eventA11y();
                                events.removeClass("open");
                                clickedButton.removeClass("open").text("Expand Events");
                                $("#hp-content-middle .upcomingevents .ui-article").css({
                                    "max-height":"150px",
                                    "overflow":"hidden",
                                    "overflow-y":"auto"
                                })
                            });
                        } else {
                            $("#hp-content-middle .upcomingevents .ui-article").css({
                                "max-height":"none",
                                "overflow":"visible"
                            })

                            var totalHeight = events.get(0).scrollHeight - 80;

                            events.animate({
                                height: totalHeight
                            }, 1000, function() {
                                events.find("a").attr("tabindex","0");
                                events.addClass("open");
                                clickedButton.addClass("open").text("Collapse Events");
                            });
                        } 
                    }
                }) 

            } else {
                //ADD VIEW CALENDAR LINK
                if($("#hp-content-middle .upcomingevents").length){
                	$("#hp-content-middle").append("

View Calendar");
                } else {
                	$("#hp-content-middle").addClass("no-events");
                }
            }

            function eventA11y(){
                $("#hp-content-middle .upcomingevents li").each(function(){
                    var elementPosition = $(this).position().top;
                    if(elementPosition > 75){

                        $(this).find("a").attr("tabindex","-1");
                    }

                });
            }
        },

