        "Search": function() {
        	// FOR SCOPE
            var template = this;

            //SEARCH INTERACTION
            $("#search-control").on('click keypress', function(event){
            	var searchControl = this;
                var searchBarWidth = $("#gb-channel-list").width() - 60;

		        if(template.AllyClickForButtons(event) === true){
					//DONT LET THE PAGE JUMP ON KEYPRESS
		            event.preventDefault();
		            if($(this).hasClass("open")){
		            	$(searchControl).removeClass("open");
                        $(searchControl).attr("aria-expanded","false");
		            	$("#gb-search").animate({
							width: "0"
						}, 500, function() {
							$("#gb-search-form").hide().attr("aria-hidden","true");
							$("#gb-search-input").attr("tabindex","-1");
						});
		            } else {
		            	$(searchControl).addClass("open");
                        $(searchControl).attr("aria-expanded","true");
		            	$("#gb-search").animate({
							width: searchBarWidth
						}, 500, function() {
							$("#gb-search-form").show().attr("aria-hidden","false");
		            		//$("#gb-search-input").attr("tabindex","0").focus();
		            	});
		        	}
	    		}
	    	})

            /* removed by Matt - case#4094250 - 02.25.19 */
            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            });

            $("#gb-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            // SUBMIT SEARCH
            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($("#gb-search-input").val() != "Search..." && $.trim($("#gb-search-input").val()) != ""){
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=8&SearchString=" + $("#gb-search-input", this).val();
                }

            });
        },

        "AllyClick": function(event) {
            if(event.type === 'click'){
                return true;
            } else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if((code === 32)|| (code === 13)){
                    return true;
                }
            } else{
                return false;
            }
        },

        "AllyClickForButtons": function(event) {
            if(event.type === 'click'){
                return true;
            }
            else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if(code === 13){
                    return true;
                }
            }
            else{
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "AccessibilityFeatures": function(){
        	$("#sw-mystart-mypasskey").attr({
                "aria-label":	"Open My Passkey",
                "role":		"button"
            });

            //REMOVE ALL HIDDEN LINKS
            $("a[data-toggle='false']").remove();
            $("li[data-toggle='#']").remove();
            $(".ac-dropdown-container.audience-links[data-toggle='false']").remove();
            $("#footer-sitename[data-toggle-sitename='false']").remove();

            $(".social-icon").show();

            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            // KEY MAPPING
            var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

            //TO TOP
            $(".to-top").keydown(function(e) {

            	// CAPTURE KEY CODE
                switch(e.keyCode) {
                	case csKeyCodes.enter:
                    case csKeyCodes.space:
                    	$("html, body").animate({ scrollTop: 0 }, "slow", function() {
                       		$("li.sw-channel-item:first-child > a").focus();
                    	});
                    break;
                }

            })



            //GLOBAL KEYBOARD NAV FOR DD SELECTORS
            $(".ac-dropdown-container .selector").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                        } else {
                            e.preventDefault();

                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");


                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true
                            }

                            switch(parentContainer){
                                case "user-options":
                                    $(".ac-mystart-button.to-top").focus();
                                break;
                                case "translate":
                                    $("#gb-logo a").focus();
                                break;
                                case "audience-one":
                                case "audience-two":
                                case "audience-three":
                                case "audience-four":
                                case "audience-five":
                                    if($(this).parent().is(":last-child")){
                                    	if($("nav").is(":hidden")){
                                        	$(".rs-gallery-control.back").focus();
                                        } else {
                                        	$("li.sw-channel-item:first-child > a").focus();
                                        }

                                    } else {
                                        $(this).parent().next().find(".selector").focus();
                                    }

                                break;
                                case "school-list":
                                	if(loggedIn == true){
                                    	$("#ui-btn-mypasskey").focus();
                                    } else {
                                    	$(".ac-mystart-button.signin a").focus();
                                    }

                                break;
                            }

                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.down:
                    case csKeyCodes.right:
                        e.preventDefault();

                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();

                    break;
                }
            });

            //GLOBAL KEYBOARD NAV FOR DD LINKS
            $(".ac-dropdown-container li a").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();

                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true
                            }

                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                      $(".ac-mystart-button.schools").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "user-options":

                                		if(loggedIn == true){
                                        	$("#ui-btn-mypasskey").focus();
                                        } else {
                                        	$(".ac-mystart-button.extra-link a").focus();
                                        }

                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "audience-one":
                                case "audience-two":
                                case "audience-three":
                                case "audience-four":
                                case "audience-five":
                                    if($(this).closest(".ac-dropdown-container").is(":first-child")){
                                        $("#gb-logo a").focus();
                                    } else {
                                        $(this).closest(".ac-dropdown-container").prev().find(".selector").focus();
                                    }

                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;


                            }
                        } else {
                            e.preventDefault();

                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true
                            }
                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                      $("#gb-logo a").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "user-options":
                                      $(".ac-mystart-button.to-top").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "audience-one":
                                case "audience-two":
                                case "audience-three":
                                case "audience-four":
                                case "audience-five":
                                    if($(this).closest(".ac-dropdown-container").is(":last-child")){
                                        $("li.sw-channel-item:first-child > a").focus();
                                    } else {
                                        $(this).closest(".ac-dropdown-container").next().find(".selector").focus();
                                    }

                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;


                            }


                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:last-child > a").focus();
                    break;

                    case csKeyCodes.esc:
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                    break;
                }
            });


            //CHANNEL DROPDOWN LINKS
            $(".mega-dd ul a").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        if($(this).parent().is(":nth-child(2)")) { //FIRST LINK IN COLUMN
                           	if($(this).closest(".channel-column").is(":first-child")){ //IN FIRST COLUMN
                            	$(this).closest(".sw-channel-dropdown").find("li:last-child li:last-child > a").focus();
                            } else {
                            	$(this).closest(".channel-column").prev().find("li:last-child > a").focus();
                            }
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        if($(this).parent().is(":last-child")){
                        	if($(this).closest(".channel-column").hasClass("sections")){ //LAST COLUMN, GO TO FIRST COLUMN

                                	$(this).closest(".sw-channel-dropdown").find(".channel-column:first-child li:nth-child(2) > a").focus();

                            } else {
                            	$(this).closest(".channel-column").next().find("li:nth-child(2) > a").focus();
                            }
                        } else {
                        	// FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();

                            $(this).closest(".sw-channel-item").prev().find("> a").focus();
                        } else {
                            e.preventDefault();

                            $(this).closest(".sw-channel-item").next().find("> a").focus();


                        }
                    break;
                }
            });



            //SEARCH
            $("#gb-search-input").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            // IS FIRST ITEM
                            $("#search-control").removeClass("open");
                            $("#search-control").attr("aria-expanded","false");
                            $("#gb-search").animate({
                                width: "0"
                            }, 500, function() {
                                $("#gb-search-form").hide().attr("aria-hidden","true");

                            });
                            $("li.sw-channel-item:last-child > a").focus();
                        } else {
                            e.preventDefault();

                            $("#search-control").focus();
                        }
                    break;
                }
            });

            $("#search-control").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            if($("#search-control").hasClass("open")){
                                $("#gb-search-input").attr("tabindex","0").focus();
                            } else {
                                // IS FIRST ITEM
                                $("#search-control").removeClass("open");
                                $("#search-control").attr("aria-expanded","false");
                                $("#gb-search").animate({
                                    width: "0"
                                }, 500, function() {
                                    $("#gb-search-form").hide().attr("aria-hidden","true");
                                    $("#gb-search-input").attr("tabindex","-1");

                                });
                                $("li.sw-channel-item:last-child > a").focus();
                            }

                        } else {
                            $("#search-control").removeClass("open");
                                $("#search-control").attr("aria-expanded","false");
                                $("#gb-search").animate({
                                    width: "0"
                                }, 500, function() {
                                    $("#gb-search-form").hide().attr("aria-hidden","true");
                                    $("#gb-search-input").attr("tabindex","-1");
                                });
                        }
                    break;
                }
            });

            //MEGA SCHOOL MENU

            //SCHOOL TABS
            $(".school-tab").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".school-tab:last-child").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).prev().focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".school-tab:first-child").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).next().focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            // IS FIRST ITEM
                            $(".ac-mystart-button.schools").focus();
                        } else {
                            e.preventDefault();

                            $(".school-listing-container.active li:first-child a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".school-tab:first-child").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".school-tab:last-child").focus();
                    break;

                    case csKeyCodes.esc:
                        $("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                        $(this).attr("aria-expanded","false");
                        $(".ac-mystart-button.schools").removeClass("open");
                        setTimeout(function(){
                            $(".ac-mystart-button.schools").focus();
                        }, 100);
                    break;
                }
            });



            //SCHOOLS
            $("li.school a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        if($(this).parent().is(":first-child")){
                            $(".school-listing-container.active").find("li.school:last-child a").focus();
                        } else {
                            $(this).parent().prev().find("> a").focus();
                        }

                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        if($(this).parent().is(":last-child")){
                            $(".school-listing-container.active").find("li.school:first-child a").focus();
                        } else {
                            $(this).parent().next().find("> a").focus();
                        }

                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".school-listing-container.active").find("li.school:first-child a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".school-listing-container.active").find("li.school:last-child a").focus();
                    break;

                    case csKeyCodes.esc:
                        $("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                        $(this).attr("aria-expanded","false");
                        $(".ac-mystart-button.schools").removeClass("open");
                        setTimeout(function(){
                            $(".ac-mystart-button.schools").focus();
                        }, 100);
                    break;
                }
            });
        }
    };

