$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=aTVs6tjvF3Qe9pvFN%2fol3Q%3d%3d&SessionID=8690d783-eb48-4a81-be26-4500c428175b&SiteID=8&ChannelID=143&SectionID=5085&PageID=15200&HitDate=4%2f22%2f2020+12%3a11%3a18+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.8.136';
