        "Search": function() {
        	// FOR SCOPE
            var _this = this;

            $(document).on("click keydown", "#gb-search-toggle", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    // SEARCH IS CLOSED
                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search").attr({"aria-hidden": "false", "data-open": "true"});

                        if(e.type == "keydown") {
                        	setTimeout(function() {
                                $("#gb-search-input").focus();
                            }, 700);
                        }
                    }

                    // SEARCH IS OPEN
                    else {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search").attr({"aria-hidden": "true", "data-open": "false"});
                    }
                }
            });

            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "What are you searching for?") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=3358&SearchString=" + $("#gb-search-input").val();
                }
            });

            $(document).on("keydown", function() {
            	setTimeout(function() {
                	if($("#gb-search[data-open='true']").length && !$("#gb-search[data-open='true']").find(":focus").length && !$("#gb-search-toggle").is(":focus")) {
                    	$("#gb-search-toggle").attr("aria-expanded", "false");
                        $("#gb-search").attr({"aria-hidden": "true", "data-open": "false"});
                    }
                },500 );
          	});

            $("#gb-search-input").focus(function() {
                if($(this).val() == "What are you searching for?") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("What are you searching for?");
                }
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

