link.script:
        $(document).ready(function () {
            //Script for Practice Tabs on Practice Details Page
            $('#nav-details li').on('click', function (e) {

                if ($(this).data('type')) {
                    var tabType = $(this).data('type');
                    if (tabType == "link") {

                    }

                    if (tabType == "content") {
                        var target = "#" + $(this).data('target');
                        $(target).html($(this).data('content'));
                        $(target).show();
                    }
                }

                if ((!$(this).data('type')) || ($(this).data('type') != "link")) {

                    $('#nav-details li').each(function () {
                        $(this).removeClass('active');
                        var target = "#" + $(this).data("target");
                        $(target).hide();
                    });

                    $(this).addClass('active');

                    var target = "#" + $(this).data("target");
                    $(target).show();
                }
            });
        });

        //Opens all outside links without target attribute in another tab
        $('a[href]').filter(function () {
            var re = /^http(.(?!ContentPage))*$/gi;
            return $(this).attr('href').match(re)
        }).each(function () {
            var hasAttr = $(this).attr('target') !== undefined;
            if (!hasAttr) {
                $(this).attr('target', '_blank');
            }
        });

         function changeFontSize(currentFontSize) {
             var currentFontSizeNum = parseFloat(currentFontSize, 10);
             var newFontSize = currentFontSizeNum * 1.3;
             $('#divContent h1,#PageText  h1, .subsite h1').css('font-size', newFontSize + 4);
             $('#divContent   h2,#PageText  h2, .subsite h2').css('font-size', newFontSize + 2);
             $('#divContent  h3,#PageText  h3, .subsite h3').css('font-size', newFontSize);
             $('#divContent   p,#PageText  p, .subsite p, .subsite div').not('#breadCrumb, #BreadcrumbListDiv, #header-search').css('font-size', newFontSize);
             $('#divContent   p,#PageText  p, .subsite p, .subsite div').not('#breadCrumb, #BreadcrumbListDiv, #header-search').css('line-height', (newFontSize + 4) + "px");
             $('#divContent  p a,#PageText  p a, .subsite p a').css('font-size', newFontSize);
             $('#divContent  ul li,#PageText  ul li').css('font-size', newFontSize);
             $('#divContent  ul li,#PageText  ul li').css('line-height', (newFontSize + 4) + "px");

             //$('html').css('font-size', newFontSize);

         }

