    function SearchFindADocFlyout() {
        $(".search-fadfo-input-name").first().autocomplete({
            minLength: 2,
            source: function (request, response) {
                $.ajax({
                    type: "POST",
                    contentType: "application/json; charset=utf-8",
                    url: "/Default.aspx/GetAutoCompleteData",
                    data: "{'searchText':'" + $(".search-fadfo-input-name").first().val() + "', 'bPhysicianOnly':'True'}",
                    dataType: "json",
                    success: function (data) {
                        response(data.d);
                    },
                    error: function (result) {
                    },
                    focus: function (event, ui) {
                       
                    }
                });
            }
        });
    }

