section.script:
    if (window.jQuery) {
        $(document).ready(function () {
            $('#list-nav-links li a').each(function () {
                if ($(this).hasClass('active')) {
                    if ($(this).data('parent')) {
                        $(this).parent().parent().show();
                    }
                    else {
                        $(this).parent().next('ul.sublinks').show();
                    }
                }

            });
        });

    }

    $(window).load(function(){
        $('.lnk-email').on('click', function (e) {
            e.preventDefault();
            var url = $(this).data('url');
            var name = $(this).data('title');

            if (name) {
                name = "Directions to " + name;
            }
            else { name = "";}

            addthis.update('share', 'url', url);
            addthis.update('share', 'title', name);
            return addthis_sendto('email');
        });
    });
