// var mn = jQuery("#top");
var SCSlogo = jQuery("#SCS-logo");
var SCSlogoRound = jQuery("#SCSlogoRound");
var moveDownTranslate = jQuery('#ct-topbar.ct-topbar');
var header = jQuery('#hp-header');
var mn = jQuery(".main-nav");


jQuery(window).scroll(function() {
if( jQuery(this).scrollTop() > 70 ) {
mn.addClass("main-nav-scrolled");
mn.addClass("slideInUp");
}
else {
mn.removeClass("main-nav-scrolled");
}
});


jQuery(window).scroll(function() {
if (jQuery(this).scrollTop() > 20) {
SCSlogo.addClass("shrink-SCS-logo");
SCSlogoRound.addClass("shrink-SCS-logo");
moveDownTranslate.addClass("moveDown");
header.addClass("up");

} else if(jQuery(this).scrollTop() > 30) {

} else {
SCSlogo.removeClass("shrink-SCS-logo");
SCSlogoRound.removeClass("shrink-SCS-logo");
moveDownTranslate.removeClass("moveDown");
// header.removeClass("up");
}
});



