    $(function() {
    	CreativeTemplate.Init();
    });

    var svgs = {
    	"arrow" : '',
    	"footer" : 'footer',
        "menu" : '',
        "pages" : '',
        "parent" : '',
        "passkey" : '',
        "student" : '',
    	"sitemap" : ''
    };

    var KeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.Translate();
            this.MyStart();
            this.Header();
            this.ChannelBar();
            this.GlobalIcons();
            if($("#hp-content").length) {
            	this.Homepage();
            }
            if($("#sp-content").length) {
            	this.PageMenu();
            }
            this.Body();
            this.Footer();
            this.Slideshow();
            this.RsMenu();
            this.Search();
            this.JsMediaQueries();

            dropdowns();

            var navO = $('nav').offset();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){ _this.Scrolling('nav', navO.top); });

            this.AppAccordion();


            $('#sw-search-button').keydown(function(e){

                var c = e.which;

                if(c == 32) {

                	$(this).click();

                }

            });

        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            var navO = $('nav').offset();
            this.CalendarListView();
            this.Scrolling('nav', navO.top);

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
            $('.more-link-under').remove();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();

        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                	this.Mobile();

                break;
                case "768":

                	this.Mobile();

                break;
                case "640":

                	this.Mobile();

                break;
                case "480":

                	this.Mobile();

                break;
                case "320":

                	this.Mobile();

                break;
            }
        },

        "Mobile": function(){

        	var width = $(window).width();

        	if(width < 1024) {

                $('#gb-mystart-right').prepend($('#gb-transalte'));
                $('#gb-mystart').addClass('cs-jc').removeClass('cs-jsb');
                $('.hp.row.one .headlines .ui-articles').removeClass('cs-jsb').addClass('cs-fw cs-jfs');
                $('#gb-schoolwires-footer-outer').append($('#gb-schoolwires-footer'));
                $('#gb-footer-options').removeClass('cs-jfe').addClass('cs-jc');
                $('.cs-events-upper, .cs-events-lower, .hp.row.three').removeClass('cs-fw');
                $('#gb-mystart-right').removeClass('cs-jsa').addClass('cs-jfe');
                $('.gb-footer-phone, .gb-footer-email').removeClass('cs-jc');
                $('.gb-footer-links').removeClass('cs-jsb').addClass('cs-jsa');
                $('.hp.row.one .headlines .ui-articles').addClass('cs-fc');
                $('.gb-footer-header').removeClass('cs-asfs');
                $('#gb-social-links').removeClass('cs-jsb').addClass('cs-jfs');
                $('.gb-social-wrapper').removeClass('cs-fc cs-jfs').addClass('cs-fc').removeClass('cs-jc');

                if($('.ui-widget-header svg').length) {

                    $('.hp.row.one .ui-widget-header, .hp.row.three .ui-widget-header, .hp.row.four .ui-widget-header').each(function(k, v){

                        if($('svg', v).length){

                            $('svg', v).remove();

                        }

                    });

                }

                if(width < 768) {

                    $('.hp.row.one .headlines .ui-articles').addClass('cs-jfs').removeClass('cs-jc');
                	$('.cs-events-upper, .cs-events-lower, .hp.row.three').addClass('cs-fw');
                	$('.gb-social-wrapper').addClass('cs-jc').removeClass('cs-jfs');

                    if($('#to-top-btn span').text() == 'Top') {

                        $('#btn-to-top span').text('Back To Top');

                    }
                    $('#gb-schoolwires-footer').addClass('cs-fc').removeClass('cs-fcc');
                    $('.gb-schoolwires-footer.links').removeClass('cs-jc');


                    if(width < 640) {

                		$('.hp.row.one .headlines .ui-articles').removeClass('cs-jfs').addClass('cs-jc');
                    	$('#gb-mystart-right').addClass('cs-jsa').removeClass('cs-jfe');
                		$('.hp.row.one .headlines .ui-articles').removeClass('cs-jsb').addClass('cs-fw cs-jfs');
                        $('#gb-social-links').addClass('cs-jsb').removeClass('cs-jfs');

                        if(!$('.ui-widget-header svg').length) {

                        	$('.hp.row.one .ui-widget-header, .hp.row.three .ui-widget-header, .hp.row.four .ui-widget-header').each(function(k, v){

                                if(!$('svg', v).length){

                                	$(v).append(svgs.arrow);

                                }

                            });

                        }

                        $('.gb-footer-phone, .gb-footer-email').addClass('cs-jc');
                        $('#gb-social-links').addClass('cs-jc').removeClass('cs-jsb');
                        $('.gb-footer-links').addClass('cs-jsb').removeClass('cs-jsa');
						$('.hp.row.one .headlines .ui-articles').removeClass('cs-fc');

                        if(width < 480) {

                        	$('#btn-to-top span').text('Top');
                            $('#gb-schoolwires-footer').removeClass('cs-fc').addClass('cs-fcc');
                        	$('.gb-schoolwires-footer.links').addClass('cs-jc');
                        }

                    }

                }

            } else {

                $('#gb-mystart-left').append($('#gb-transalte'));
                $('#gb-mystart').removeClass('cs-jc').addClass('cs-jsb');
                $('.hp.row.one .headlines .ui-articles').addClass('cs-jsb').removeClass('cs-fw cs-jfs');
                $('#gb-schoolwires-footer-outer > .gb-section').prepend($('#gb-schoolwires-footer'));
                $('#gb-footer-options').addClass('cs-jfe').removeClass('cs-jc');
                $('.cs-events-upper, .cs-events-lower, .hp.row.three').removeClass('cs-fw');
                $('#gb-mystart-right').removeClass('cs-jsa').addClass('cs-jfe');
                $('.gb-footer-phone, .gb-footer-email').removeClass('cs-jc');
                $('.gb-footer-links').removeClass('cs-jsb').addClass('cs-jsa');
                $('.hp.row.one .headlines .ui-articles').addClass('cs-fc');
                $('.gb-social-wrapper').addClass('cs-fc cs-jfs').removeClass('cs-fc');

                if($('.ui-widget-header svg').length) {

                    $('.hp.row.one .ui-widget-header, .hp.row.three .ui-widget-header, .hp.row.four .ui-widget-header').each(function(k, v){

                        if($('svg', v).length){

                            $('svg', v).remove();

                        }

                    });

                }

                var wW = $(window).width();

                if(wW > 1255) {

                    wW = (wW - 1215) / 2;

                    $('#hp-slideshow .rs-gallery-desc').css('left', wW + 'px');
                    $('#hp-slideshow .rs-gallery-control.next').css('right', wW + 'px');
                    $('#hp-slideshow .rs-gallery-control.back').css('right', (wW + 40) + 'px');

                } else {
                    $('#hp-slideshow .rs-gallery-desc').css('left', '20px');
                    $('#hp-slideshow .rs-gallery-control.next').css('right', '20px');
                    $('#hp-slideshow .rs-gallery-control.back').css('right', '60px');
                }

            }

        },

        "Scrolling": function(container, offset) {

            var wScroll = $(window).scrollTop(),
            	wW = $(window).width();

            if(offset < 170) {

            	offset = 170;

            }

        	if(wScroll >= offset && wW <= 1024) {

                $(container).addClass('fixed');
                $('main').css('margin-top', $(container).height() + 'px');

            } else if(wW < 1024) {

                $(container).removeClass('fixed');
                $('main').css('margin-top', '0px');

            } else if(wScroll >= (offset + 200)&& wW < 1024) {

                $(container).addClass('fixed');
                $('main').css('margin-top', $(container).height() + 'px');

            }

        },

