            $('.gb-mystart-button a').keydown(function(e){

            	var c = e.which;

            	if(c == KeyCodes.left || c == KeyCodes.right) {

                	e.preventDefault();

                    if(c == KeyCodes.right && $(this).parent().next().children().html() != undefined) {

                    	$(this).parent().next().children().first().focus();

                    } else if(c == KeyCodes.right && $('#gb-mystart-right a').first().html() != undefined) {

                    	$('#gb-mystart-right a').first().focus();

                    } else if(c == KeyCodes.left && $(this).parent().prev().children().html() != undefined) {

                    	$(this).parent().prev().children().first().focus();

                    } else if(c == KeyCodes.left && $('#gb-mystart-left .gb-mystart-dropdown').last().children().first().html() != undefined) {

                    	$('#gb-mystart-left .gb-mystart-dropdown').last().children().first().focus();

                    }

                }

            });



        },

        "Header": function() {
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/TX01817643/Centricity/Template/GlobalAssets/images///logos/BISD-logo-RGB-Small.png");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('');
                } else {
                    $("#gb-logo").append('');
                }
            }

            // Region C BG Image
            var rCBGSrc = $.trim("/cms/lib/TX01817643/Centricity/Template/GlobalAssets/images///background/Skyline.jpg");
            if(showLogo) {
                var rCBGSplit = rCBGSrc.split("/");
                var rCBGSplitLen = rCBGSplit.length;
                if((logoSrc != "") && (rCBGSplit[rCBGSplitLen - 1] != "default-man.jpg")) {
                    $(".hp.row.two").css('background-image', 'url(' + rCBGSrc + ')');
                } 
            }

            var siteName = 'Beaumont Independent School District',
            	tagline = 'Preparing Our Next Generation';

            if(!false) {

                $('#gb-footer-name').append('
' + siteName + '' + '
' + tagline + '');

            } else {

                if(false) {

                	$('#gb-footer-name, #gb-sitename').append('
' + siteName + '');

                }

                if(false) {

                	$('#gb-footer-name, #gb-sitename').append('
' + tagline + '');

                } else {

                	$('#gb-footer-name').append('
' + tagline + '');

                }

            }

        },

