            //--> Added to Footer Two
            var extras = [
                    [true, "Accreditation", "/Page/615"],
                    [false, "Extra Link 2", "#ExtraLink2"],
                    [false, "Extra Link 3", "#ExtraLink3"],
                    [false, "Extra Link 4", "#ExtraLink4"]
                ],
                gbIcons = [
                    ["Find it Fast", "/Page/2412", "_self"],
                    ["Join Our Team", "https://bmtisd.tedk12.com/hire/index.aspx", "_self"],
                    ["Transportation Routes", "/zones", "_self"],
                    ["Contact Us", "/page/249", "_self"],
                    ["Web TV", "/page/2619", "_self"],
                    ["Online Registration", "/Page/5644", "_self"],
                    ["Child Nutrition", "/domain/99", "_self"],
                    ["Calendar", "/Page/5531", "_self"]
                ],
                popLinks1 = '',
                popLinks2 = '',
                n = 0;

            for (var icon in gbIcons) {

                if(icon < iconNum) {

                    if(n == 0) {

                        popLinks1 += '
' + gbIcons[icon][0] + '';

                        n++;

                    } else {

                        popLinks2 += '
' + gbIcons[icon][0] + '';

                        n = 0;

                    }

                }

            }

            for (var link in extras) {

                if(extras[link][0]) {

                    if(n == 0) {

                        popLinks1 += '
' + extras[link][1] + '';

                        n++;

                    } else {

                        popLinks2 += '
' + extras[link][1] + '';

                        n = 0;

                    }

                }

            }

            popLinks1 += '';

            popLinks2 += '';

            $('.gb.footer.two .gb-footer-links').html(popLinks1 + popLinks2);

        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });
        },

        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                //DOC add signed out breadcrumb
                $(".ui-breadcrumbs").append("
Signed Out");
            }

            // ADJUST SIGN IN PAGE
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

            // ADJUST REGISTER PAGE
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            // ADJUST SEARCH RESULTS PAGE
            if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
                $("#sw-content-layout-wrapper").children().wrapAll('

');
                $(".ui-breadcrumbs").append("
Search Results");
            }

            // ADD READ FULL STORY BUTTON TO EACH HEADLINE
            $(".ui-widget.app.headlines").each(function(){
                if($(this).closest('.hp.row.one')){
                    $(".ui-article", this).each(function(){
                        var headlineURL = $("h1 a", this).attr("href");
                        $(".ui-article-content", this).append('' + svgs.arrow + 'Read More');
                    });
                    // ADJUST MORE LINKS
                    $("a.more-link span", this).html("VIEW ALL " + svgs.arrow);
                    $('.ui-widget-detail', this).append($('a.more-link', this));
                } else {
                    $(".ui-article", this).each(function(){
                        var headlineURL = $("h1 a", this).attr("href");
                        $(".ui-article-description", this).append('Read Full Story »');
                    });
                    // ADJUST MORE LINKS
                    $("a.more-link span", this).html("VIEW ALL " + svgs.arrow);
                    $("a.more-link").wrap('
');
                }
            });

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");


            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //--> Check if Site Shortcuts App is using images.
            $('.ui-widget.app.siteshortcuts .site-shortcuts > li').each(function(k, v){

                //--> If using images remove bullet
                if($('img', v).length) {

                	$(v).addClass('has-image');

                }

            });

            //MOD EVENTS
            $(".upcomingevents").modEvents({
                columns:"yes",
                monthLong:"no",
                dayWeek:"yes"
            });

            eventsByDay(".upcomingevents .ui-articles");
            if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
                $("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
            }

            //--> Home Page Region C Upcoming Events Tab Build
            if($('.hp.row.two .upcomingevents').length){ //--> Check if app is there.

                UETabs('.hp.row.two', 'Take a look at the exciting events happening within our district', 5);

            }


            //--> Change View Calendar Button
            $('.view-calendar-link').each(function(k, v){

                if(!$(v).closest('.hp.row.one')){

                	$(v).html('View Full Calendar ' + svgs.arrow);

                }

            });


        },

