                '
' + $('#sw-footer-copyright').text() + '';

            $(".gb-schoolwires-footer.links").html(bbLinks);

            //--> To Top
            $('#btn-to-top').click(function(e){

            	e.preventDefault();
                $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                    $("#gb-mystart-outer a").first().focus();
                });

            });

            $('#gb-footer-options a:contains("Site Map")').addClass('cs-fc cs-aic').attr('id', 'gb-sitemap').prepend(svgs.sitemap);



            	$('#gb-btn-signin').attr('href', $('#ui-btn-signin a').attr('href'));
            	$('#gb-btn-register').attr('href', $('#ui-btn-register a').attr('href'));



            $('#sw-footer-outer').remove();

        },

