        "Search": function() {
            //SEARCH TEXT
            $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder").attr("value", "Search...");

            $("div#sw-mystart-search input#sw-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            //--> Search Functionality
            $('#gb-search-dropdown').append($('#sw-mystart-search'));

            //--> Search GO btn text
            $('#sw-search-button').addClass('cs-fc cs-aic').html('GO');

            $('#gb-search-btn').click(function(e){

                e.preventDefault();

            	if($(this).hasClass('open')) {

                	$(this).removeClass('open').attr('aria-expanded', 'false').attr('aria-label', 'Open search');
                    $('#gb-search-dropdown').attr('aria-hidden', 'true').slideUp();

                } else {

                	$(this).addClass('open').attr('aria-expanded', 'true').attr('aria-label', 'Close search');
                    $('#gb-search-dropdown').attr('aria-hidden', 'false').slideDown();
                    $('#sw-search-input').delay(500).focus();
                    $('#sw-search-button').removeAttr('title').attr('aria-label', 'Submit search input');

                }

            }).keydown(function(e){

            	var c = e.which;
                if(c == KeyCodes.enter){
                	e.preventDefault(); //--> Capture keyup instead so Input does not run.
                }

            }).keyup(function(e){

            	var c = e.which;

                if(c == KeyCodes.enter){
                	e.preventDefault();
                	$(this).click();
                }

            });

        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "Translate": function() {
        	$("#gb-translate-dropdown").creativeTranslate({
                "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                "advancedOptions": {
                	"addMethod": "prepend", // PREPEND OR APPEND THE TRANSLATE ELEMENT
                    "dropdownHandleText": "Translate this page...", // ONLY FOR FRAMESET AND API VERSIONS
                    "useTranslatedNames": true, // ONLY FOR FRAMESET AND API VERSIONS - true = TRANSLATED LANGUAGE NAME, false = ENGLISH LANGUAGE NAME
                    "customElement": { // ONLY FOR FRAMESET AND API VERSIONS
                        "useCustomElement": true,
                        "translateItemsList": true, // true = THE TRANSLATE ITEMS WILL BE AN UNORDERED LIST, false = THE TRANSLATE ITEMS WILL JUST BE A COLLECTION OF  TAGS
                        "customElementMarkup": "[$CreativeTranslateListItems$]" // CUSTOM HTML MARKUP THAT MAKES THE CUSTOM TRANSLATE ELEMENT/STRUCTURE - USE [$CreativeTranslateListItems$] ACTIVE BLOCK IN THE MARKUP WHERE THE TRANSLATE ITEMS SHOULD BE ADDED
                    },
                    "apiKey": "", // ONLY FOR API VERSION
                    "brandedLayout": 1 // 1 = VERTICAL (TEXT UNDER SELECT), 2 = HORIZONTAL (TEXT BESIDE SELECT), 3 = NON-RESPONSIVE MEGA DROPDOWN
                },
                "translateLoaded": function() {} // CALLBACK TO RUN YOUR CUSTOM CODE AFTER EVERYTHING IS READY
            });
        }
    };

    function UETabs(loc, cap, max) {

    	var KeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };


        var events = [],
            ueHtml = '',
            panels = '',
            caption = cap;


        $(loc + ' .upcomingevents .ui-article').each(function(k, v){ //--> Get the Data

            if(k < max){ //--> Collect no more then 5 Days

                var day = $('.joel-day', v).text();

                $('.joel-day', v).remove(); //--> Remove so I can get the number

                var date = $('.joel-date', v).text();
                var info = [];

                //--> Get times and titles
                $('.ui-article-description', v).each(function(m, n){

                    info.push({"time": $('.sw-calendar-block-time', n).text(), "title": $('.sw-calendar-block-title', n).text(), "link": $('.sw-calendar-block-title a', n).attr('href')});

                });

                //--> Push all the events info to the array
                events.push({"month": $('.joel-month', v).text(), "day": day, "date": date, "info": info});

            }

        });

        //--> Get app header if shown
        if(!$( loc + ' .upcomingevents .ui-widget-header').hasClass('ui-helper-hidden')) {
            var header = $(loc + ' .upcomingevents .ui-widget-header').html();
            $(loc + ' .ui-widget-header').remove();
        }

        //--> Check if there is a caption
        if(caption.trim() != '' ){
            caption = '
' + cap + '';
        }

		//--> Start building
        ueHtml += '

' + //--> Wrappers
            '
' + header + caption + ''; //--> App Header with caption text.

        //--> Build out day buttons.
        for(var event in events) {

            if(event == 0) { //--> Make first button and panel active.


                ueHtml += '' +
                    '

' + events[event].day + '' + //--> Add Day
                    '

' + events[event].month + ' ' + events[event].date + ''; //--> Add Month and Date - Close Button

                panels += '
' +
                    '
';

                for(var i in events[event].info) { //--> Add each event to this pannel.

                    panels += '
' + events[event].info[i].time + '' + events[event].info[i].title + '';

                }

                panels += ''; //--> Button up the pannel

            } else {

                ueHtml += '' +
                    '

' + events[event].day + '' + //--> Add Day
                    '

' + events[event].month + ' ' + events[event].date + ''; //--> Add Month and Date - Close Button

                panels += '
' +
                    '
';

                for(var i in events[event].info) { //--> Add each event to this pannel.

                    panels += '
' + events[event].info[i].time + '' + events[event].info[i].title + '';

                }

                panels += ''; //--> Button up the pannel

            }

        }

        ueHtml += '

' +
            panels + '' +
            '' +
            '
VIEW FULLCALENDAR' + svgs.arrow + '';

        //--> Add Everything in.
        $(loc + ' .upcomingevents .ui-widget-detail').html(ueHtml);

        $('.cs-tab').unbind().click(function(e){

        	//--> Get My Variables
        	var tab = $(this).data('tab');

            e.preventDefault();

            if($(this).hasClass('active')) {

            	$('#cs-panel-' + tab + ' a').first().focus();

            } else {

                $('.cs-panel').removeClass('active').attr('aria-hidden', 'true').hide();
                $('.cs-tab').removeClass('active');
                $(this).addClass('active');
                $('#cs-panel-' + tab).addClass('active').show().attr('aria-hidden', 'false');
                $('#cs-panel-' + tab + ' a').first().focus();

            }
        }).keydown(function(e){

            var c = e.which;

            if(c == KeyCodes.enter || c == KeyCodes.space || c == KeyCodes.down || c == KeyCodes.right || c == KeyCodes.left) {

                e.preventDefault();

                if(c == KeyCodes.enter || c == KeyCodes.space || c == KeyCodes.down) {

                	$(this).click();

                } else if(c == KeyCodes.right) {

                	var tab = $(this).data('tab'); //--> Get current tab number

                    if($('#cs-tab-' + (tab + 1)).length) { //--> Make sure there is a tab there

                    	$('#cs-tab-' + (tab + 1)).focus();

                    } else {

                    	$('.cs-tab').first().focus(); //--> Focus first tab

                    }

                } else if(c == KeyCodes.left) {

                	var tab = $(this).data('tab'); //--> Get current tab number);

                    if($('#cs-tab-' + (tab - 1)).length) { //--> Make sure there is a tab there

                    	$('#cs-tab-' + (tab - 1)).focus();

                    } else {

                    	$('.cs-tab').last().focus(); //--> Focus last tab

                    }

                }

            }


        });

        //--> Controls for inside the panel
        $('.cs-panel a').keydown(function(e){

        	var link = $(this).data('link'),
            	panel = $(this).data('panel'),
                c = e.which;

            if(c == KeyCodes.up || c == KeyCodes.down) {

            	e.preventDefault();

                if(c == KeyCodes.up) {

                    //--> Make sure link above exists
                	if($('a[data-panel="' + panel + '"][data-link="' + (link - 1 ) + '"]').length) {

                        $('a[data-panel="' + panel + '"][data-link="' + (link - 1 ) + '"]').focus();

                    } else {

                    	$('#cs-tab-' + panel).focus();

                    }

                } else if(c == KeyCodes.down) {

                    //--> Make sure link below exists
                	if($('.cs-panel a[data-panel="' + panel + '"][data-link="' + (link + 1 ) + '"]').length) {

                        $('.cs-panel a[data-panel="' + panel + '"][data-link="' + (link + 1 ) + '"]').focus();

                    } else {

                    	$('.cs-panel.active a').first().focus();

                    }

                }

            }

        });

    }



    function dropdowns(){

        //--> Dropdown funciton
        var closeDrop;

        $('.gb-mystart-btn').unbind().click(function(){

            var dropdown = $(this).attr('aria-controls'),
            	btnW = $(this).width();

            if($(this).hasClass('open') && $(this).hasClass('inner') ){

				if(dropdown == 'gb-account-dropdown') {

                	$('#' + dropdown + ' a').last().delay(500).focus();

                } else {

                	$('#' + dropdown + ' a').first().delay(500).focus();

                }

            } else if($(this).hasClass('open')) {

                $('#' + dropdown).removeClass('open').attr('aria-hidden', 'true').stop(true, true).slideUp();
                $(this).removeClass('open').attr('aria-expanded', 'false');

            } else {

                $('#' + dropdown).addClass('open').attr('aria-hidden', 'false').stop(true, true).slideDown();
                $(this).addClass('open').attr('aria-expanded', 'true');
                $('.gb-dropdown a').attr('tabindex', '-1');

                if(btnW < 160) {

                    $('#' + dropdown).css('left', '-' + (btnW - 81) + 'px');

                }

            }


        }).hover(function(){

            if(!$(this).hasClass('open')) {

                $(this).click();

            } else {

                clearTimeout(closeDrop);

            }

        }, function(){

            var id = $(this).attr('id');

            closeDrop = setTimeout(function(){

                $('#' + id).click();

            }, 300);

        }).keydown(function(e){

            var c = e.which;

            if(c == KeyCodes.down || c == KeyCodes.enter || c == KeyCodes.space || c == KeyCodes.up){

                e.preventDefault();
                $(this).addClass('inner').click();

            }

            if(c == KeyCodes.left || c == KeyCodes.right) {

                	e.preventDefault();

                    if($(this).attr('id') != 'gb-account-btn') {

                        if(c == KeyCodes.right && $(this).parent().next().children().html() != undefined) {

                            $(this).parent().next().children().first().focus();

                        } else if(c == KeyCodes.right && $('#gb-mystart-right a').first().html() != undefined) {

                            $('#gb-mystart-right a').first().focus();

                        } else if(c == KeyCodes.left && $(this).parent().prev().children().html() != undefined) {

                            $(this).parent().prev().children().first().focus();

                        } else if(c == KeyCodes.left && $('#gb-mystart-left .gb-mystart-dropdown').last().children().first().html() != undefined) {

                            $('#gb-mystart-left .gb-mystart-dropdown').last().children().first().focus();

                        }

                    }

                }

        }).focus(function(){


            var id = $(this).attr('id');

            if(!$('#' + id).hasClass('open')) {

                $('#' + id).click();

            } else {

                clearTimeout(closeDrop);

            }


        }).blur(function(){

            var id = $(this).attr('id');

            closeDrop = setTimeout(function(){

                $('#' + id).click();

            }, 300);

        });

        $('.gb-dropdown').unbind().hover(function(){

            clearTimeout(closeDrop);

        }, function(){

            var id = $(this).attr('id').replace('dropdown', 'btn');

            closeDrop = setTimeout(function(){

                $('#' + id).click();

            }, 300);

        });

        $('.gb-dropdown a').unbind().keydown(function(e){

            var c = e.which,
                first = $(this).parent().first().children(),
                last = $(this).parent().last().children(),
                next = $(this).parent().next().children(),
                prev = $(this).parent().prev().children(),
                btn = $('#' + $(this).closest('.gb-dropdown').attr('id').replace('dropdown', 'btn'));

            if(c == KeyCodes.down || c == KeyCodes.up || c == KeyCodes.left || c == KeyCodes.right) {

                e.preventDefault();

                if((c == KeyCodes.down && next.html() == undefined) && ($(this).closest('.gb-dropdown').attr('id') != 'gb-account-dropdown')) {

                    first.focus();

                } else if((c == KeyCodes.down && next.html() == undefined) && ($(this).closest('.gb-dropdown').attr('id') != 'gb-account-dropdown')) {

                    btn.removeClass('inner').focus();

                } else if((c == KeyCodes.up && prev.html() == undefined) && ($(this).closest('.gb-dropdown').attr('id') == 'gb-account-dropdown')) {

                    last.focus();

                } else if((c == KeyCodes.down && next.html() == undefined) && ($(this).closest('.gb-dropdown').attr('id') == 'gb-account-dropdown')) {

                    btn.removeClass('inner').focus();

                } else if(c == KeyCodes.down) {

                    next.focus();

                } else if(c == KeyCodes.up) {

                    prev.focus();

                } else if(c == KeyCodes.right && $(this).closest('.gb-dropdown').attr('id') != 'gb-account-dropdown') {

                    if(btn.parent().next().children('button').html() != undefined) {

                    	btn.parent().next().children('button').focus();
                    	btn.removeClass('inner').click();

                    } else {

                		btn.removeClass('inner').focus();
                    	$('#gb-mystart-right a').first().focus();

                    }

                } else if( c == KeyCodes.left && $(this).closest('.gb-dropdown').attr('id') != 'gb-account-dropdown') {

                	btn.removeClass('inner').focus();

                }

            }

        }).focus(function(){

            clearTimeout(closeDrop);

        }).blur(function(){


            var id = $(this).closest('.gb-dropdown').attr('id').replace('dropdown', 'btn');

            closeDrop = setTimeout(function(){

                $('#' + id).removeClass('inner').click();

            }, 100);

        });

        //--> Channel Item dropdown adjustment
        $('.sw-channel-item').hover(function(){

        	var w = $(this).width();

            if(w < 200) {

            	$('.sw-channel-dropdown', this).css('left', '-' + (203 - w) / 2 +'px');

            } else {

            	$('.sw-channel-dropdown', this).css({

                	'left' : '0px',
                    'right' : '0px',
                    'margin' : 'auto'

                });

            }

        });

    }


    function eventsByDay(container) {
        var countLi = 0;
        var csMonth = "";
        $(".ui-article", container).each(function(){
            if (!$(".sw-calendar-block-date", this).length){
                var moveArticle = $(this).html();
                $(this).parent().prev().children().children().next().append(moveArticle);
                $(this).parent().remove();
            } else {
                $(".sw-calendar-block-date", this).wrapInner('');
                $(".joel-month", this).parents(".sw-calendar-block-date").prepend($(".joel-month", this));
            }

            //Add 'ALL DAY' Text to full day events
            $('.upcoming-column.right .ui-article-description', this).each(function(){ 
                if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                    $(this).prepend('ALL DAY');
                }
            });
        });

    }

