	(function () {
    var url = "/apps/js/google_tag_manager/google_tag_manager.js";
    var gtm = document.createElement('script');
    gtm.type = 'text/javascript';
    gtm.async = true;
    gtm.src = ('https:' == document.location.protocol ? 'https://' : 'http://') + document.location.hostname + url;
		var s = document.body.firstChild;
		s.parentNode.insertBefore(gtm, s);
	})();

