        "Search": function() {
        	var defSearchText = "SEARCH SOMETHING";

            var searchBox = $('#swsitesearch-txt-searchstring');

            searchBox
                .attr("aria-label", "Search input")
                .attr("placeholder", '')
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }

                    /* If the search trigger isn't to the right of the search input. 
                    searchBox.off('keyup keydown keypress').on('keydown', function(e) { // Trigger the click event from the keyboard
                        var code = e.which;
                        // 13 = Return, 32 = Space
                        if ((code === 9) ) {
                            e.preventDefault();
                            $('#gb-search-trigger').focus();
                        }
                    });
                    */
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }

                    //searchBox.off('keydown keyup keypress');
                });

			//$('#gb-search-trigger').attr('onclick', $('#search-container input[type="button"]').attr('onclick'));
        },
        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },
        "StickyHeader": function() {
        	var getWidth = $(window).width();

            if (getWidth >= 1024) {
                // CHANNEL BAR
                var gbNavOffSet = $("#gb-sticky-header-outer").offset().top;

                if ($(window).scrollTop() <= gbNavOffSet) {
                    if(CreativeTemplate.csNavIsSticky) {
                        $("#gb-sticky-header-outer, #gb-page").removeClass("sticky-on");
                        $("#gb-sticky-header-outer").css("height", "auto");
                        CreativeTemplate.csNavIsSticky = 0;
                    }
                } else {
                    $("#gb-sticky-header-outer").css("height", $("#gb-sticky-header").outerHeight(true));

                    if(!CreativeTemplate.csNavIsSticky) {
                        $("#gb-sticky-header-outer, #gb-page").addClass("sticky-on");
                        CreativeTemplate.csNavIsSticky = 1;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top", $("#gb-sticky-header").outerHeight(true));
                        }
                    }

                    setTimeout(function(){
                        $("#gb-sticky-header-outer").css("height", $("#gb-sticky-header").outerHeight(true));
                    }, 300);
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + (($("#gb-channel-list-outer").outerHeight(true) > 150) ? $("#gb-channel-list-outer").outerHeight(true) : 175) + "px"
                });

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("#gb-sticky-header").outerHeight(true));
                    }
                }
            } else {
                if(CreativeTemplate.csNavIsSticky == 1) {
                    $("#gb-sticky-header-outer, #gb-page").removeClass("sticky-on");
                    $("#gb-sticky-header-outer").css("height", "auto");

                    CreativeTemplate.csNavIsSticky = 0;
                }

                $("#sw-maincontent").removeAttr("style");
            }
        },


// PLUGIN METHODS
        "Slideshow": function() {
        	if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1440,
                    "imageHeight" : 900,
                    "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 1.5,
                    "transitionDelay" : 4,
                    "fullScreenRotator" : true,
                    "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {

                        //Custom description transition method
                        $('#hp-slideshow .mmg-description').css('display', 'none');
                        if(props.mmgRecords[props.nextRecordIndex].hideTitle == "False" || props.mmgRecords[props.nextRecordIndex].hideCaption == "False" ){
                        	$('#hp-slideshow .mmg-description').stop(true).fadeIn(750);
                        }


                    	if(!$("#hp-slideshow .mmg-description-outer .mmg-description-links span").length) {
                             $("#hp-slideshow .mmg-description-outer .mmg-description-links .mmg-description-link").wrapInner("");
                        }
                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {
                    	$('#hp-slideshow .mmg-description-outer').wrapInner('
');

                        $('#hp-slideshow .mmg-description-inner').each(function(descIndex, desc){
                        	if( $('.mmg-description-links', desc).length <= 0 ){
                            	$(desc).append('
');
                            }
                        });

                        $('#hp-slideshow .mmg-description-link').wrapInner('');

                        if( $('#hp-slideshow .mmg-description-outer').length > 0 ){
	                        $('#hp-slideshow .mmg-description-outer').after('

');
						}else{
                        	$('#hp-slideshow .mmg-container').append('

');
                        }

                        if( $('#hp-slideshow .mmg-control').length > 0 ){
                            if( $('#hp-slideshow .mmg-control.next').length > 0 ){
								$('#hp-slideshow .mmg-controls-container')
                                	.append($('#hp-slideshow .mmg-control.back'))
                                	.append($('#hp-slideshow .mmg-control.next'));
                            }

                            if( $('#hp-slideshow .mmg-control.play-pause').length > 0 ){
                            	$('#hp-slideshow .mmg-controls-container').append($('#hp-slideshow .mmg-control.play-pause'));
                            }
                        }

                        $('#hp-slideshow .mmg-controls-container').append($('#hp-scroll-down'));
                    }, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {
                    	if(!$("#hp-slideshow .mmg-description-outer .mmg-description-links span").length) {
                             $("#hp-slideshow .mmg-description-outer .mmg-description-links .mmg-description-link").wrapInner("");
                        }
                    } // props.element, props.mmgRecords
                });
            }
        },
        "SLM": 	function() {
        	$.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 1685,
                'cache': false,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                    $('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#school-list');
                    CreativeTemplate.RsMenu(schoolList);
                }
            });
        },
