$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=oZY44A64tgqS1rEvvHXnPA%3d%3d&SessionID=026babe0-4599-4adb-83f5-209ef7dc6eaf&SiteID=4&ChannelID=80&SectionID=109&PageID=7002&HitDate=4%2f22%2f2020+11%3a24%3a14+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.10.76';
