function setRequireJsOnError()
{
    requirejs.onError = function(err)
    {
        if (err.requireType === 'timeout') {
            alert('The application took too long to load. Please check your internet connection and then try again.');
            return; //don't throw an exception or there will be another cryptic alert later on via window.onerror handler
        }

        throw err;
    };
}
