function doSplash(){
    // Must use pure JS here, no jquery
    var spinner = new Spinner({color: "#949699", top: "3px", radius: 12, length: 6, width: 6, lines: 8});
    spinner.spin(document.getElementById("appSpinner"));

    // If we are not mobile, add the desktop classes to the splash loader
    if (!isMobile())
    {
        var element = document.getElementsByClassName("appViewPort");
        element[0].classList.add("desktop");
    }
}
