body.script:
    	$(document).ready(function () {
    		skipNavigation('skip-nav'); // send container id
    		$('#skip-nav').focus(function () {
    			$('#skip-nav').slideDown();

    		});

    		$(window).focus(function () {
    			$('#skip-nav').show();
    			$('#skip-nav').focus();
    		});

    		$("#skipToNav").click(function (event) {
    			// strip the leading hash and declare
    			// the content we're skipping to
    			var skipTo = "#" + this.href.split('#')[1];
    			$(skipTo).trigger('focus');
    			$('html,body').animate({ scrollTop: $(this.hash).offset().top }, 500);
    			return false;
    		});
    		$("#skipToContent").click(function (event) {
    			event.preventDefault();
    			var skipTo = "#" + this.href.split('#')[1];
    			$('html,body').animate({ scrollTop: $(this.hash).offset().top }, 500);
    			$(skipTo).trigger('focus');
    			return true;
    		});
    		$("#skipToPolicy").click(function (event) {
    			event.preventDefault();
    			var skipTo = "#" + this.href.split('#')[1];
    			$('html,body').animate({ scrollTop: $(this.hash).offset().top }, 500);
    			$(skipTo).trigger('focus');
    			return true;
    		});
    		$("#skipToBrowse").click(function (event) {
    			event.preventDefault();
    			var skipTo = "#" + this.href.split('#')[1];
    			$('html,body').animate({ scrollTop: $(this.hash).offset().top }, 500);
    			$(skipTo).trigger('focus');
    			return true;
    		});
    		$("#skipToTopic").click(function (event) {
    			event.preventDefault();
    			var skipTo = "#" + this.href.split('#')[1];
    			$('html,body').animate({ scrollTop: $(this.hash).offset().top }, 500);
    			$(skipTo).trigger('focus');
    			return true;
    		});

    		$(document).on('keyup keypress', 'form input[type="text"]', function (e) {
    			if (e.which == 13) {
    				e.preventDefault();
    				return false;
    			}
    		});

    		table = $('#results').dataTable({
    			"bJQueryUI": false,
    			"bSort": false,
    			"bFilter": false,
    			"bLengthChange": false,
    			"oLanguage": {
    				"sSearch": "Filter within results (Displayed text only):"
    			},
    			"sDom": '<"top"lf>rt<"bottom"ip><"clear">',
    			"sPaginationType": "full_numbers"
    		});


    		$('input[type="text"]').focus(function () {
    			$(this).addClass("focus");
    		});

    		$('input[type="text"]').blur(function () {
    			$(this).removeClass("focus");
    		});

    		$('a').has('img').addClass('img');

    		$("#SearchTerm").keyup(function (e) {
    			var code = e.which; // recommended to use e.which, it's normalized across browsers
    			if (code == 13) {
    				e.preventDefault();
    				verifyValidSearch();
    			}
    		});


    		$("#SearchBtn").click(function (event) {
    			event.preventDefault();
    			verifyValidSearch();
    		});

    		function verifyValidSearch() {
    			var searchFilter = $('#SearchTerm').val();
    			var theId = '784';
    			if (searchFilter != "") {
    				$.getJSON('/Policy/IsSearchValid', { id: theId, filter: searchFilter }, function (data) {
                		if (data == false) {
                			displaySearchError(searchFilter);
                		} else {
                			$('#Search').trigger('click');
                		}
                	});
                }
			}

    		function displaySearchError(searchFilter) {
    			$('#SearchErrorModal').html("
" + "No matches found for '" + searchFilter + "'." + "");
    			$('#SearchErrorModal').dialog({
    				modal: 'true',
    				title: 'Not Found',
    				autoOpen: 'true',
    				resizable: false,
    				buttons: {
    					Close: function () {
    						$(this).dialog("close");
    					}
    				}
    			});
    			$('#SearchErrorModal').show();
    		}


    		$("input[id$='GoBtn']").click(function (event) {
    			event.preventDefault();
    			var searchFilter = $('#PolicyCode').val();
    			var theId = '784';
        		if (searchFilter != "") {
        			$.getJSON('/Policy/IsValidCode', { id: theId, filter: searchFilter }, function (data) {
                		if (data == false) {
                			$('#SearchErrorModal').html("
" + "No matches found for policy code " + searchFilter.toUpperCase() + "." + "");
                			$('#SearchErrorModal').dialog({
                				modal: 'true',
                				title: 'Not Found',
                				autoOpen: 'true',
                				resizable: false,
                				buttons: {
                					Close: function () {
                						$(this).dialog("close");
                					}
                				}
                			});
                			$('#SearchErrorModal').show();
                		} else {
                			$('#Go').trigger("click");
                		}
                	});
                }

        	});

    	});


		function checkValue(tb) {
			if (tb.value == "") {
				return false;
			}

			return true;
		}

		//function showSkipMenu() {
		//    $('#skipMenuShortcut').hide();
		//    $('#skipMenu').focus();
		//}

		$('#adv-search-button').click(function () {
			if (!$('#Phrase').val() && !$('#All').val() && !$('#Any').val() && !$('#None').val())
				return false;

			var anyChecked = false;
			$('input[type=checkbox]:checked').each(
			function () {
				anyChecked = true;
			});

			if (!anyChecked)
				return false;

			return true;
		});
