          function ResourceHtmlEncode(s,en) {
            if(s){
              //s = s.replace(/"/g, '"');
              //s = s.replace(/'/g, "'");

              en = en || true;
              // do we convert to numerical or html entity?
              if(en){
                //s = s.replace(/\'/g, "'"); //no HTML equivalent as &apos is not cross browser supported
                //s = s.replace(/\"/g, """);
                s = s.replace(/\&/g, "&");
                s = s.replace(//g, ">");
              }
              else{
                //s = s.replace(/\'/g, "'"); //no HTML equivalent as &apos is not cross browser supported
                //s = s.replace(/\"/g, """);
                s = s.replace(/\&/g, "&");
                s = s.replace(//g, ">");
              }

              return s;
            }
            else{
              return "";
            }
          }

          function ResourceCleanUpUL() {
            //Delete empty ul
            $('ul.ResourceUL').each(function () {
              if($(this).has("li").length == 0) {
                var RemoveUL = $(this)[0].dataset.categoryul;

                //Removed the ResourceTabContent
                document.getElementById(RemoveUL).remove();

                //Removed the ResourceTabList
                $('li.nav-item a#' + RemoveUL + '-tab').parent().remove();
              }
            });

            //Hide the whole Resource section if there are no listitems.
            if ($('ul.ResourceUL li').length == 0) {
              $('.DnnModule-36151').remove();
            }
          }

          function ResourceConfirm(msg)
          {
            return confirm(msg);
          }

