          function ResourceGetOrderClicked() {
            //alert(JSON.stringify(DnD.GetOrder('.ResourceTabUL', 'li', 'id')));
            //alert(JSON.stringify(DnD.GetOrder('.ResourceUL', 'li', 'id')));
            var ItemCategoryOrder = (JSON.stringify(DnD.GetOrder('.ResourceTabUL', 'li', 'id')));
            var ItemOrder = (JSON.stringify(DnD.GetOrder('.ResourceUL', 'li', 'id')));

            $.ajax({
              url: location.protocol + "//" + location.host + '/DesktopModules/XModPro/Feed.aspx',
              type: 'POST',
              dataType: 'html',
              data: {
                "xfd": "ContentResourceOrderUpdate",
                "pid": 0,
                "PortalID": 84,
                "EmpGUID": GUID,
                "ContentItemID": 75381,
                "ItemCategoryOrder": ItemCategoryOrder,
                "ItemOrder": ItemOrder
              }
              , success: function (data) {
                $('#EditResourceSortOrder').show();
                $('#SaveResourceSortOrder').hide();
                alert("Resource Sort Order Changed");
                window.location = window.location.href;
              }
            });
          }

          function ResourceSetup()
          {
            $('#SaveResourceSortOrder').hide();
            BuildTabList();
            //InsertResources();
          }

          function BuildTabList()
          {
            var categories = [{"ResourceCategoryID":8135,"Category":"Coronavirus (COVID-19) Information","CategorySansSpace":"CoronavirusCOVID19Information"},{"ResourceCategoryID":6449,"Category":"General","CategorySansSpace":"General"}];
            var active = true;
            categories.forEach(function(category)
                               {
                                 //var categorySansSpace = category.Category.replace(/\s/g, '');

                                 //NavLink(category.Category, category.CategoryOrder, categorySansSpace, active);
                                 NavLink(category.Category, category.ResourceCategoryID, category.CategorySansSpace, active);
                                 TabPane(category.CategorySansSpace, active);
                                 InsertResources(category.CategorySansSpace);
                                 active = false;
                               });
          }

          function NavLink(category, resourceCategoryID, categorySansSpace, active)
          {
            var activeclass = '';
            var expanded = 'false';
            if(active)
            {
              expanded = 'true';
              activeclass = ' active show';
            }
            else {
              activeclass = 'inactive';
            }
            $("#ResourceTabList").append('
'+ ResourceHtmlEncode(category, true) +'');
          }

          function TabPane(categorySansSpace, active)
          {
            var activeclass = '';
            if(active)
            {
              activeclass = ' active show';
            }
            $("#ResourceTabContent").append('



');
          }

          function InsertResources(categorySansSpace)
          {
            $('[data-Category='+categorySansSpace+']').appendTo('[data-categoryul='+categorySansSpace+']');
          }

