    function AddContentClick()
    {
        
        $('#AddContentButtonContainer').prop("hidden", true);
        $('#AddContentFormContainer').prop("hidden", false);
        $('#txtPageTitle').prop('required', true);
    }

    function CreateContentClick(isEdit)
    {
        var valid = CCISDContent.ValidateForm() && CCISDContent.SlugIsValid('txtPageTitle', isEdit);

        if (valid === false)
        {
            event.preventDefault();
            event.stopPropagation();
        }
        else
        {
            ContentFormClose();
        }

        //mainForm.classList.add('was-validated');

        return valid;
    }

    function ContentFormClose()
    {        
        $('#AddContentButtonContainer').prop("hidden", false);
        $('#AddContentFormContainer').prop("hidden", true);
        $('#txtPageTitle').prop('required', false);

    }




    




    function CreateSelectOptionGroups()
    {
        var groups = {};

        $("select option[data-category]").each(function ()
        {
             groups[$.trim($(this).attr("data-category"))] = true;
        });

        $.each(groups, function (c) {
             $("select option[data-category='"+c+"']").wrapAll('');
        });


        $('#ddlBanner').change(function ()
        {            
            UpdatePreviewImage();
        });
        
        UpdatePreviewImage();
    }

    function UpdatePreviewImage()
    {
            var $sel = $('#ddlBanner' + ' option:selected');
            var src = $sel.data("file");
            $('#SelectedBannerImage').attr("src", src);
            //console.log(SelectedImageID);
    }

    $(document).ready(function ()
    {
        if ($('#ddlBanner').length)
        {
            CreateSelectOptionGroups();
        }

        //Move the Section Edit Pane to the appropriate EditPanePlaceHolder for each page type.
        $('#SectionEditPane').appendTo('.EditPanePlaceHolder').first();
                    
    });

