	$(function() {
    	CreativeTemplate.Init(); //tip of the iceberg
    });


    var CreativeTemplate = {

    	/*****
        	PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrcLight": "/cms/lib/TX01001475/Centricity/Template/24/images/dallas-isd-logo-light.png",
        "DefaultLogoSrcDark": "/cms/lib/TX01001475/Centricity/Template/24/images/dallas-isd-logo-dark.png",
        "DropdownTimeout": "", //Will store the CustomDropdown timeout object that will get passed among the dropdown functions.
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,
        "csNavIsSticky": 0,



        /*****
        	METHODS
        *****/

// BIG BANG
        "Init": function() {
        	$('[data-hide=""], [data-hide="!false"], [data-hide="true"]').remove();

            this.HighContrastBar();
            this.SetTemplateProps();
            this.JsMediaQueries();
            this.RsMenu();
            this.MyStart();
            this.Search();
            this.Header();
            this.CustomDropdowns();
            this.ChannelBar();
            this.Body();
            this.ArticleSliders();
            this.GlobalIcons();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.AppAccordion();
            this.Footer();
            csGlobalJs.OpenInNewWindowWarning();

            if( this.DynamicStyles != "" ){ //Build out our stylesheet if any of the above items have added styles to it.
            	this.BuildStyleSheet();
            }

            $(window)
            	.load(function(){ 
                	CreativeTemplate.WindowLoad(); 
				})
            	.resize(function(){ 
                     CreativeTemplate.WindowResize(); 
                     CreativeTemplate.CalendarListView();
                })
                .scroll(function(){
                    CreativeTemplate.WindowScroll();
                });
        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            this.CalendarListView();
			this.Search();
            csGlobalJs.OpenInNewWindowWarning();

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },
        "WindowScroll": function(){ 
        	CreativeTemplate.StickyHeader();

        	if( $('#hp-stats').hasClass('needs-animated') ){
                var sT = $('#hp-stats').offset().top
                    wS = $(window).scrollTop();
                    wH = $(window).height();

                if (wS >= (sT - (wH * 0.5))){
                   CreativeTemplate.AnimateStatNumbers();
                   $('#hp-stats').removeClass('needs-animated');
                }
        	}
        },


//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            if( isNaN(this.GetBreakPoint()) ){
            	//Desktop

            }else{
            	//BP's
            }
        },
        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },
        "CustomDropdowns": function() {
        	CreativeTemplate.SpecialDropdowns();

        	/* customDropdowns
            **  Use: Sets up custom dropdown menus.
            */
            $('.custom-dropdown .dropdown a').keydown(function(e){
                if( e.keyCode == 9 ){
                    CreativeTemplate.ClearDropdowns();
                }else if( e.keyCode == 27){
                	e.preventDefault();
                	$('.custom-dropdown > button[aria-expanded="true"]').focus();
                    CreativeTemplate.ClearDropdowns();
				}
            });

        	$('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);
            $('.custom-dropdown button').on('keydown', function(e) { // Trigger the click event from the keyboard
                var code = e.which;
                // 13 = Return, 32 = Space
                if ((code === 13) || (code === 32)) {
                    e.preventDefault();

                    $(this).click();
                    if( !$(this).parent().hasClass('openMenu') ){
                        //clear our keyboard hooks since the menu is closing.
                        CreativeTemplate.DropdownKeyboardFunctionality($('~ .dropdown', this), true);
                    }else{
                        //add our keyboard hooks
                        CreativeTemplate.DropdownKeyboardFunctionality($('~ .dropdown', this));
                    }
                }else if(code == 39 || code == 40 && $(this).attr('aria-expanded') == 'true'){
                    e.preventDefault();
                    $('~ .dropdown a', this).first().focus();
                }else if( code == 9 ){
                	if($(this).attr('aria-controls') != 'translate-option-list'){
                		CreativeTemplate.ClearDropdowns();
					}
                }
            });

            //Custom Dropdown Functionality
            $('.custom-dropdown > button').off('click').click(function(e){
                clearTimeout(this.DropdownTimeout);

                if( $(this).attr("aria-expanded") == "false" ){
                    CreativeTemplate.ClearDropdowns()
                    $('~ .dropdown', this).attr('aria-hidden', 'false').slideDown();
                    $(this).attr('aria-expanded', 'true');
                    $(this).parent().addClass("openMenu");
                }else{
                    CreativeTemplate.ClearDropdowns();
                }
            });

            $('.custom-dropdown').hover(function(e){ //In
                clearTimeout(CreativeTemplate.DropdownTimeout);
            },function(e){ //Out
                //canDropDownHide = true;
                CreativeTemplate.DropdownTimeout = setTimeout(function(){
                    CreativeTemplate.ClearDropdowns();
                }, 800);
            });
        }, /* Self-Made Dropdown - Main Function and Event Handlers*/
        "ClearDropdowns": function(){
            $('.custom-dropdown.openMenu .dropdown').attr('aria-hidden', 'true').slideUp();
            $('.custom-dropdown.openMenu > button').removeClass('acc-open').attr('aria-expanded', 'false');
            $('.custom-dropdown.openMenu li').removeClass('active-list-item');
            this.DropdownKeyboardFunctionality($('.custom-dropdown.openMenu .dropdown'), true);
            $('.custom-dropdown.openMenu').removeClass("openMenu");
        }, /* Self-Made Dropdown - Clears the dropdowns */
        "DropdownKeyboardFunctionality": function(dropdown, clear){
        	if( clear ){
                //$('a', dropdown).off('keydown');
            }else{
                $('a', dropdown).first().trigger('focus');

                $('a', dropdown).keydown(function(e){
                    var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                    var linkObj = $('a', dropdown);

                    switch(e.keyCode) {
                        // CONSUME LEFT AND UP ARROWS
                        case csKeyCodes.left:
                        case csKeyCodes.up:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                                // IS FIRST ITEM
                            if(linkObj.index($(this)) == 0) {
                                $(dropdown).siblings('button').focus();
                            } else {
                                linkObj[linkObj.index($(this)) - 1].focus();
                            }
                        break;

                        // CONSUME RIGHT AND DOWN ARROWS
                        case csKeyCodes.right:
                        case csKeyCodes.down:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS LAST ITEM
                            if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                                // FOCUS FIRST ITEM
                                linkObj[0].focus();
                            } else {
                                // FOCUS NEXT ITEM
                                linkObj[linkObj.index($(this)) + 1].focus();
                            }
                        break;

                        default: //pass
                        break;
                    }
                });
            }
        }, /* Self Made Dropdown - a11y Event Handlers */
        "HandleTabEvent": function(tab, e, element) {
            var lastIndex = $(".cs-tab", element).length - 1;

            // ONLY CONSUME IF NOT USING ALT KEY
            if (!e.altKey) {
                switch(e.keyCode) {
                    // MOVE ONE STEP BACKWARDS
                    case 37 : // LEFT ARROW
                    case 38 : // UP ARROW
                        if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
                            e.preventDefault();
                            e.stopPropagation();
                            var switchTabs = true;

                            // GET THE PREVIOUS TAB
                            var prevTab;
                            if (parseInt(tab.attr("data-index")) == 0) { 
                                //prevTab = $(".cs-tab[data-index='" + lastIndex + "']", element); // LAST TAB
                                $('#cs-panel-close-button').focus();
                                switchTabs = false;
                            } else { 
                                prevTab = $(".cs-tab[data-index='" + (parseInt(tab.attr("data-index")) - 1) + "']", element); // PREVIOUS TAB
                            } 

                            // SWITCH THE TABS
                            if(switchTabs) CreativeTemplate.SwitchTabs(tab, prevTab, element);
                        }
                    break;

                    // MOVE ONE STEP FORWARDS
                    case 39 : // RIGHT ARROW
                    case 40 : // DOWN ARROW
                        if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
                            e.preventDefault();
                            e.stopPropagation();
                            var switchTabs = true;

                            // GET THE NEXT TAB
                            var nextTab;
                            if (parseInt(tab.attr("data-index")) == lastIndex) { 
                                //nextTab = $(".cs-tab[data-index='0']", element); // FIRST TAB
                                $('#cs-panel-close-button').focus();
                                switchTabs = false;
                            } else { 
                                nextTab = $(".cs-tab[data-index='" + (parseInt(tab.attr("data-index")) + 1) + "']", element); // PREVIOUS TAB
                            } 

                            // SWITCH THE TABS
                            if(switchTabs) CreativeTemplate.SwitchTabs(tab, nextTab, element);
                        }
                    break;

                    case 9 : //Tab Key
                    	e.preventDefault();
                    	$('.cs-panel[aria-hidden="false"] a', element).first().focus();
                    break;

                    // JUMP TO FIRST TAB
                    case 36 : // HOME KEY
                        e.preventDefault();
                        e.stopPropagation();

                        CreativeTemplate.SwitchTabs(tab, $(".cs-tab[data-index='0']", element));
                    break;

                    // JUMP TO LAST TAB
                    case 35 : // END KEY
                        e.preventDefault();
                        e.stopPropagation();

                        CreativeTemplate.SwitchTabs(tab, $(".cs-tab[data-index='" + lastIndex + "']", element));
                    break;
                }
            }
        },
        "HandlePanelEvent": function(panelLink, e, element) {
            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
            var linkObj = $('.cs-panel[aria-hidden="false"] a', element);

            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                        // IS FIRST ITEM
                    if(linkObj.index(panelLink) == 0) {
                        $('.cs-tab[aria-selected="true"]', element).focus();
                    } else {
                        linkObj[linkObj.index(panelLink) - 1].focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if(linkObj.index(panelLink) == (linkObj.length - 1) ) {
                        // FOCUS FIRST ITEM
                        linkObj[0].focus();
                    } else {
                        // FOCUS NEXT ITEM
                        linkObj[linkObj.index(panelLink) + 1].focus();
                    }
                break;

                case csKeyCodes.tab:
                	e.preventDefault();
                    $('#cs-panel-close-button').focus();
				break;

                default: //pass
                break;
        	}
        },
        "SwitchTabs": function(currTab, nextTab, element) {
            // SWITCH TAB
            $(currTab).removeClass("active").attr("aria-selected", "false").attr("tabindex", "-1");
            $(nextTab).addClass("active").attr("aria-selected", "true").attr("tabindex", "0").focus();

            // SWITCH PANEL
            var nextIndex = parseInt(nextTab.attr("data-index"));
            $("div.ui-widget.app.activeTabApp", element).removeClass("active-app");
            $("div.ui-widget.app.activeTabApp:eq(" + nextIndex + ")", element).addClass("active-app");

            $(".cs-panel", element).attr("aria-hidden", "true");
            $(".cs-panel[data-index='" + nextIndex + "']", element).attr("aria-hidden", "false");
        },


// SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "BuildStylesheet": function() {
        	var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var rules = document.createTextNode(this.DynamicStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = this.DynamicStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },


// GETTERS
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }, 
        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
        	return (h.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


