        "Search": function() {
        	$('#gb-search-trigger').off('click').click(function(e){
            	if( $(this).attr('aria-expanded') == "false" ){
                	$(this).attr('aria-expanded', 'true');

                    $('#gb-search')
                    	.attr('aria-hidden', 'false')
                        .show(400, function(e){ $('#gb-search-input').focus(); });
                }else{
                	$(this).attr('aria-expanded', 'false');

                    $('#gb-search').attr('aria-hidden', 'true').stop(true).hide(400);
                }
            });


        	var defSearchText = "Search...";

            var searchBox = $('#gb-search-input');
            var sbTimer = '';

            searchBox
                .attr("placeholder", defSearchText)
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                    clearTimeout(sbTimer);
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }

                    if($(this).val() == "" || $(this).val() == defSearchText){
                    	sbTimer = setTimeout(function(e){
                            $('#gb-search-trigger').attr('aria-expanded', 'false');
                            $('#gb-search').attr('aria-hidden', 'true').stop(true).hide(400);
                    	}, 400);
                    }
                })
                .keydown(function(e){
                	if(e.keyCode == "9" && ($(this).val() == "" || $(this).val() == defSearchText)){
                    	$('#gb-search-trigger').attr('aria-expanded', 'false');
                    	$('#gb-search').attr('aria-hidden', 'true').stop(true).hide(400);
                    }
                });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();
                if($("#gb-search-input").val() != "" && $("#gb-search-input").val() != defSearchText) {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
            });
        },
        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },
        "AnimateStatNumbers": function(){
            $('.stat-number.needs-animated').each(function(statIndex, stat){
                var number = $(stat).attr('data-stat-number');

                if(number.match(/(rd|th|nd)/)){
                    //count down add 30 to number and countdown
                    var ending = number.match(/(rd|th|nd)/)[0];
                    var animatingNumber = parseFloat(number) + 20;
                    jQuery({ Counter: animatingNumber }).animate({ Counter: parseFloat(number) }, {
                        duration: 1400,
                        easing: 'swing',
                        step: function () {
                        	$(stat).removeClass('needs-animated').html(Math.floor(this.Counter) + ending);
                        }
                    });
                }else{
                    //count up
                    var ending = number.match(/\D+/);
					var animatingNumber = ((parseFloat(number) - 20) >= 0) ? parseFloat(number) - 20 : 0;
                    jQuery({ Counter: animatingNumber }).animate({ Counter: parseFloat(number) }, {
                        duration: 1400,
                        easing: 'swing',
                        step: function () {
                        	$(stat).removeClass('needs-animated').html(Math.ceil(this.Counter) + ending);
                        }
                    });
                }
            });
        },
        "StickyHeader": function() {
            var getWidth = $(window).width();

            if (getWidth >= 1024) {
                // CHANNEL BAR
                var gbNavOffSet = $(".sticky-wrapper-outer").offset().top;

                if ($(window).scrollTop() <= gbNavOffSet) {
                    if(CreativeTemplate.csNavIsSticky) {
                        $(".sticky-wrapper-outer, #gb-page").removeClass("sticky-on");
                        $(".sticky-wrapper-outer").css("height", "auto");
                        CreativeTemplate.csNavIsSticky = 0;
                    }
                } else {
                    $(".sticky-wrapper-outer").css("height", $(".sticky-wrapper").outerHeight(true));

                    if(!CreativeTemplate.csNavIsSticky) {
                        $(".sticky-wrapper-outer, #gb-page").addClass("sticky-on");
                        CreativeTemplate.csNavIsSticky = 1;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top", $(".sticky-wrapper").outerHeight(true));
                        }
                    }

                    setTimeout(function(){
                        $(".sticky-wrapper-outer").css("height", $(".sticky-wrapper").outerHeight(true));
                    }, 300);
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + (($(".sticky-wrapper-outer").outerHeight(true) > 150) ? $(".sticky-wrapper-outer").outerHeight(true) : 175) + "px"
                });
            } else {
                if(CreativeTemplate.csNavIsSticky == 1) {
                    $(".sticky-wrapper-outer, #gb-page").removeClass("sticky-on");
                    CreativeTemplate.csNavIsSticky = 0;
                }
                $(".sticky-wrapper-outer").css("height", '');

                $("#sw-maincontent").removeAttr("style");
            }
        },
